INSERT INTO `alembic_version` (`version_num`) VALUES
('508f9801867f');
INSERT INTO `author` (`id`, `name`, `intro`, `birth_year`, `death_year`, `dynasty_id`, `updated_at`) VALUES
(10001, '苏轼', '苏轼，北宋文学家、书画家。字子瞻，又字和仲，又称大苏，号东坡居士。眉州眉山（今属四川）人。与父苏洵、弟苏辙合称三苏。他在文学艺术方面堪称全才。其文汪洋恣肆，明白畅达，与欧阳修并称欧苏，为唐宋八大家之一；诗清新豪健，善用夸张比喻，在艺术表现方面独具风格，与黄庭坚并称苏黄；词开豪放一派，对后代很有影响，与辛弃疾并称苏辛；书法擅长行书、楷书，能自创新意，用笔丰腴跌宕，有天真烂漫之趣，与黄庭坚、米芾、蔡襄并称宋四家；画学文同，喜作枯木怪石，论画主张神似。著有《苏东坡全集》和《东坡乐府》等。', '1037', '1101', 10001, '2014-07-23 19:53:14'),
(10003, '辛弃疾', '辛弃疾，南宋词人，原字坦夫，改字幼安，别号稼轩，历城（今山东济南）人。出生时，中原已为金兵所占。21岁参加抗金义军，不久归南宋。历任湖北、江西、湖南、福建、浙东安抚使等职。一生力主抗金。曾上《美芹十论》与《九议》，条陈战守之策。其词抒写力图恢复国家统一的爱国热情，倾诉壮志难酬的悲愤，对当时执政者的屈辱求和颇多谴责；也有不少吟咏祖国河山的作品。题材广阔又善化用前人典故入词，风格沉雄豪迈又不乏细腻柔媚之处。作品集有《稼轩长短句》，今人辑有《辛稼轩诗文钞存》。 ', '1140', '1270', 10001, '2014-07-23 19:53:14'),
(10004, '归有光', '归有光，明代官员、散文家。字熙甫，又字开甫，别号震川，又号项脊生，汉族，江苏昆山人。嘉靖十九年举人。会试落第八次，徙居嘉定安亭江上，读书谈道，学徒众多，60岁方成进士，历长兴知县、顺德通判、南京太仆寺丞，留掌内阁制敕房，与修《世宗实录》，卒于南京。归有光与唐顺之、王慎中两人均崇尚内容翔实、文字朴实的唐宋古文，并称为嘉靖三大家。由于归有光在散文创作方面的极深造诣，在当时被称为“今之欧阳修”，后人称赞其散文为“明文第一”，著有《震川集》、《三吴水利录》等。', '1506', '1571', 10007, '2014-07-23 19:53:14'),
(10005, '白居易', '白居易，字乐天，晚年又号香山居士，河南新郑（今郑州新郑）人，我国唐代伟大的现实主义诗人，中国文学史上负有盛名且影响深远的诗人和文学家。他的诗歌题材广泛，形式多样，语言平易通俗，有“诗魔”和“诗王”之称。官至翰林学士、左赞善大夫。有《白氏长庆集》传世，代表诗作有《长恨歌》、《卖炭翁》、《琵琶行》等。白居易故居纪念馆坐落于洛阳市郊。白园（白居易墓）坐落在洛阳城南香山的琵琶峰。 ', '772', '846', 10006, '2014-07-23 19:53:14'),
(10006, '老子', '老子即李耳，字聃，一字或曰谥伯阳。楚国苦县历乡曲仁里（今河南省鹿邑县太清宫镇）人，约生活于前571年至471年之间。是我国古代伟大的哲学家和思想家、道家学派创始人，被唐朝帝王追认为李姓始祖。老子故里苦县亦因老子先后更名为真源县、卫真县、鹿邑县，并在鹿邑留下许多与老子相关的珍贵文物。老子乃世界文化名人，世界百位历史名人之一，存世有《道德经》（又称《老子》），其作品的精华是朴素的辨证法，主张无为而治，其学说对中国哲学发展具有深刻影响。在道教中老子被尊为道祖。 ', '-571', '-458', 10005, '2014-08-20 23:31:21'),
(10007, '张岱', '张岱，又名维城，字宗子，又字石公，号陶庵、天孙，别号蝶庵居士，晚号六休居士，汉族，山阴（今浙江绍兴）人。寓居杭州。出生仕宦世家，少为富贵公子，精于茶艺鉴赏，明亡后不仕，入山著书以终。张岱为明末清初文学家、史学家，其最擅长散文，著有《琅嬛文集》《陶庵梦忆》《西湖梦寻》《三不朽图赞》《夜航船》等绝代文学名著。', '1597', '1679', 10007, '2014-07-23 19:53:14'),
(10008, '沈复', '沈复 ，字三白，号梅逸，清乾隆二十八年生于长洲（今江苏苏州）。著有《浮生六记》。工诗画、散文。据《浮生六记》来看，他出身于幕僚家庭，没有参加过科举考试，曾以卖画维持生计。乾隆四十二年（公元1777年）随父亲到浙江绍兴求学。乾隆四十九年（公元1784年），乾隆皇帝巡江南，沈复随父亲恭迎圣驾。后来到苏州从事酒业。他与妻子陈芸感情甚好，因遭家庭变故，夫妻曾旅居外地，历经坎坷。妻子死后，他去四川充当幕僚。此后情况不明。', '1763', '1825', 10008, '2014-07-23 19:53:14'),
(10009, '李商隐', '李商隐，著名诗人。擅长诗歌写作，骈文文学价值也很高，他是晚唐最出色的诗人之一，和杜牧合称“小李杜”，与温庭筠合称为“温李”，因诗文与同时期的段成式、温庭筠风格相近，且三人都在家族里排行第十六，故并称为“三十六体”。其诗构思新奇，风格秾丽，尤其是一些爱情诗和无题诗写得缠绵悱恻，优美动人，广为人传诵。但部分诗歌过于隐晦迷离，难于索解，至有“诗家总爱西昆好，独恨无人作郑笺”之说。因处于牛李党争的夹缝之中，一生很不得志。死后葬于家乡沁阳（今沁阳与博爱县交界之处）。作品收录为《李义山诗集》。', '812', '858', 10006, '2014-07-23 19:53:14'),
(10011, '陶渊明', '陶渊明，字元亮（又一说名潜，字渊明），号五柳先生，私谥靖节，东晋末期南朝宋初期诗人、文学家、辞赋家、散文家。汉族，东晋浔阳柴桑人。曾做过几年小官，后辞官回家，从此隐居，田园生活是陶渊明诗的主要题材，相关作品有《饮酒》、《归园田居》、《桃花源记》、《五柳先生传》、《归去来兮辞》等。', '352', '427', 10015, '2014-07-23 19:53:14'),
(10012, '曹操', '曹操，字孟德，小字阿瞒，沛国谯人，汉族。东汉末年著名政治家、军事家、文学家、书法家。三国中曹魏政权的缔造者，先为东汉大将军 、丞相，后为魏王。其子曹丕称帝后，追尊其为魏武帝。一生以汉朝大将军、丞相的名义征讨四方割据政权，为统一中国北方做出重大贡献，同时在北方屯田，对农业生产恢复有很大作用。曹操的诗作具有创新精神，开启并繁荣了建安文学，给后人留下了宝贵的精神财富，史称建安风骨。鲁迅评价其为“改造文章的祖师”。', '155', '220', 10014, '2014-07-23 19:53:14'),
(10013, '高濂', '高濂，字深甫，号瑞南，钱塘（今浙江杭州）人，明万历年间的名士、戏曲家、养生家及书籍收藏家。工诗词及戏曲，藏书丰富，“少婴赢疾，复苦瞆眼”，高濂喜欢谈医道，重养生，咨访奇方秘药，用以治疗赢疾，眼疾遂愈。曾在北京鸿胪寺任官，后隐居西湖。高濂平生著作甚丰，主要有《玉簪记》、《节孝记》、《遵生八笺》、《草花谱》、《野蔌品》、《四时幽赏》、《四时逸事》、《艺花谱》、《兰谱》等。', '1573', '1620', 10007, '2014-07-23 19:53:14'),
(10014, '卢纶', '卢纶，字允言，唐代诗人，大历十才子之一，汉族，河中蒲（今山西省永济县）人。天宝末举进士，遇乱不第；代宗朝又应举， 屡试不第。大历六年，宰相元载举荐，授阌乡尉；后由王缙荐为集贤学士，秘书省校书郎，升监察御史。出为陕府户曹、河南密县令。后元载、王缙获罪，遭到牵连。德宗朝复为昭应令，又任河中浑瑊元帅府判官，官至检校户部郎中。有《卢户部诗集》。', '737', '799', 10006, '2014-07-23 19:53:14'),
(10015, '朱熹', '朱熹， 字元晦、一字仲晦，号晦庵、晦翁、考亭先生、云谷老人、沧洲病叟、逆翁。 汉族，祖籍南宋江南东路徽州府婺源县（今江西省婺源），出生于南剑州尤溪。19岁进士及第，曾任荆湖南路安抚使，仕至宝文阁待制。为政期间，申敕令、惩奸吏、治绩显赫。南宋著名的理学家、思想家、哲学家、教育家、诗人、闽学派的代表人物，世称朱子，是孔子、孟子以来最杰出的弘扬儒学的大师。', '1130', '1200', 10001, '2014-07-23 19:53:14'),
(10016, '岳飞', '岳飞，字鹏举，汉族。北宋相州汤阴县永和乡孝悌里（今河南省安阳市汤阴县菜园镇程岗村）人。中国历史上著名战略家、军事家、民族英雄、抗金名将。岳飞在军事方面的才能则被誉为宋、辽、金、西夏时期最为杰出的军事统帅、连结河朔之谋的缔造者。同时又是两宋以来最年轻的建节封侯者。南宋中兴四将（岳飞、韩世忠、张俊、刘光世）之首。', '1103', '1142', 10001, '2014-07-23 19:53:14'),
(10017, '李煜', '李煜，五代十国时南唐国君，961年-975年在位，字重光，初名从嘉，号钟隐、莲峰居士。彭城（今江苏徐州）人。南唐元宗李璟第六子，于宋建隆二年（961年）继位，史称李后主。开宝八年，宋军破南唐都城，李煜降宋，被俘至汴京，封为右千牛卫上将军、违命侯。后因作感怀故国的名词《虞美人》而被宋太宗毒死。李煜虽不通政治，但其艺术才华却非凡。精书法，善绘画，通音律，诗和文均有一定造诣，尤以词的成就最高。千古杰作《虞美人》、《浪淘沙》、《乌夜啼》等词。在政治上失败的李煜，却在词坛上留下了不朽的篇章，被称为“千古词帝”。', '937', '978', 10020, '2014-07-23 19:53:14'),
(10018, '欧阳修', '欧阳修，字永叔，号醉翁，晚年又号“六一居士”，吉州永丰（今江西吉安永丰）人，自称庐陵人。谥号文忠，世称欧阳文忠公，北宋卓越的政治家、文学家、史学家，与（唐朝）韩愈、柳宗元、（宋朝）王安石、苏洵、苏轼、苏辙、曾巩合称“唐宋八大家”。后人又将其与韩愈、柳宗元和苏轼合称“千古文章四大家”。', '1007', '1072', 10001, '2014-07-23 19:53:14'),
(10019, '陆游', '陆游，南宋诗人，字务观，号放翁，越州山阴（今浙江绍兴）人。少时受家庭爱国思想熏陶，高宗时应礼部试，为秦桧所黜。孝宗时赐进士出身。中年入蜀，投身军旅生活，官至宝章阁待制。晚年退居家乡，但收复中原信念始终不渝。创作诗歌很多，今存九千多首，内容极为丰富。抒发政治抱负，反映人民疾苦，风格雄浑豪放；抒写日常生活，也多清新之作。词作量不如诗篇巨大，但和诗同样贯穿了气吞残虏的爱国主义精神。杨慎谓其词纤丽处似秦观，雄慨处似苏轼。著有《剑南诗稿》、《渭南文集》、《南唐书》、《老学庵笔记》等。 ', '1125', '1210', 10001, '2014-07-23 19:53:14'),
(10020, '纳兰性德', '纳兰性德，满洲人，字容若，号楞伽山人，是清代最为著名的词人之一。他的诗词不但在清代词坛享有很高的声誉，在整个中国文学史上，也以“纳兰词”在词坛占有光采夺目的一席之地。他生活于满汉融合的时期，其贵族家庭之兴衰具有关联于王朝国事的典型性。他虽侍从帝王，却向往平淡的经历。这一特殊的生活环境与背景，加之他个人的超逸才华，使其诗词的创作呈现独特的个性特征和鲜明的艺术风格。', '1655', '1685', 10008, '2014-08-20 23:39:42'),
(10021, '司空曙', '司空曙，字文明，或作文初。广平（今河北永年县东南）人，大历十才子之一，唐代诗人。约唐代宗大历初前后在世。大历年进士，磊落有奇才，与李约为至交。性耿介，不干权要。家无担石，晏如也。尝因病中不给，遣其爱姬。韦辠节度剑南，辟致幕府。授洛阳主簿。未几，迁长林县丞。累官左拾遗。终水部郎中。其诗朴素真挚，情感细腻，多写自然景色和乡情旅思，诗风闲雅疏淡。', '720', '790', 10006, '2014-07-23 19:53:14'),
(10025, '毛泽东', '毛泽东，字润之，笔名子任。湖南湘潭人。中国革命家、战略家、理论家、诗人，中国共产党、中国人民解放军和中华人民共和国的主要缔造者和领袖，毛泽东思想的主要创立者。从1949年到1976年，毛泽东是中华人民共和国的最高领导人。他对马克思列宁主义的发展、军事理论的贡献以及对共产党的理论贡献被称为毛泽东思想。毛泽东被视为现代世界历史中最重要的人物之一，《时代》杂志将他评为20世纪最具影响100人之一。', '1893', '1976', 10022, '2014-07-23 19:53:14'),
(10026, '诸葛亮', '诸葛亮，字孔明，号卧龙，汉族，琅琊阳都（今山东临沂市沂南县）人，三国时期蜀汉丞相、杰出的政治家、军事家、发明家、文学家。在世时被封为武乡侯，死后追谥忠武侯，东晋政权特追封他为武兴王。诸葛亮为匡扶蜀汉政权，呕心沥血，鞠躬尽瘁，死而后已。其代表作有《前出师表》、《后出师表》、《诫子书》等。曾发明木牛流马等，并改造连弩，可一弩十矢俱发。于234年在陕西省岐山县五丈原逝世。成都、汉中、南阳等地有武侯祠，杜甫作《蜀相》赞诸葛亮。', '181', '234', 10014, '2014-07-23 19:53:14'),
(10027, '孔子', '孔子，名丘，字仲尼，东周时期鲁国陬邑（今中国山东曲阜市南辛镇）人，先祖为宋国（今河南商丘）贵族。春秋末期的思想家和教育家、政治家，儒家思想的创始人。孔子集华夏上古文化之大成，在世时已被誉为“天纵之圣”、“天之木铎”，是当时社会上的最博学者之一，被后世统治者尊为孔圣人、至圣、 至圣先师、万世师表，是“世界十大文化名人”之首。孔子的儒家思想对中国和朝鲜半岛、日本、越南等地区有深远的影响。', '-551', '-479', 10005, '2014-08-21 08:01:57'),
(10029, '孟浩然', '孟浩然，唐代诗人，孟子第33代。本名不详（一说名浩），字浩然，襄州襄阳（今湖北襄阳）人，世称“孟襄阳”。浩然，少好节义，喜济人患难，工于诗。年四十游京师，唐玄宗诏咏其诗，至“不才明主弃”之语，玄宗谓：“卿自不求仕，朕未尝弃卿，奈何诬我？”因放还未仕，后隐居鹿门山，著诗二百余首。孟浩然与另一位山水田园诗人王维合称为“王孟”。 ', '689', '740', 10006, '2014-07-23 19:53:14'),
(10030, '孟郊', '孟郊，唐代诗人。字东野。汉族，湖州武康（今浙江德清）人，祖籍平昌（今山东临邑东北），先世居洛阳（今属河南）。唐代著名诗人。现存诗歌500多首，以短篇的五言古诗最多，代表作有《游子吟》。有“诗囚”之称，又与贾岛齐名，人称“郊寒岛瘦”。元和九年，在阌乡（今河南灵宝）因病去世。张籍私谥为贞曜先生。', '751', '814', 10006, '2014-10-17 23:14:47'),
(10031, '屈原', '屈平，字原，通常称为屈原，又自云名正则，号灵均，汉族，战国末期楚国丹阳（今湖北秭归）人，楚武王熊通之子屈瑕的后代。屈原虽忠事楚怀王，却屡遭排挤，怀王死后又因顷襄王听信谗言而被流放，最终投汨罗江而死。屈原是中国最伟大的浪漫主义诗人之一，也是我国已知最早的著名诗人，世界文化名人。他创立了“楚辞”这种文体，也开创了“香草美人”的传统。代表作品有《离骚》、《九歌》等。', '-340', '-278', 10005, '2014-07-23 19:53:14'),
(10032, '范仲淹', '范仲淹，字希文，汉族，北宋著名的政治家、思想家、军事家和文学家，世称“范文正公”。生于武宁军（治所徐州）（一说河北真定府）。祖籍邠州（今陕西省彬县），先人迁居苏州吴县（今江苏苏州），唐朝宰相范履冰的后人。他为政清廉，体恤民情，刚直不阿，力主改革，屡遭奸佞诬谤，数度被贬。1052 年（皇祐四年）五月二十日病逝于徐州，终年64岁。是年十二月葬于河南伊川万安山，谥文正，封楚国公、魏国公。有《范文正公全集》传世，通行有清康熙岁寒堂刻版本，附《年谱》及《言行拾遗事录》等。', '989', '1052', 10001, '2014-07-23 19:53:14'),
(10033, '王羲之', '王羲之，东晋书法家，字逸少，号澹斋，汉族，原籍山东琅琊（今山东临沂），后迁居会稽（绍兴），写下《兰亭集序》，晚年隐居会稽下辖剡县金庭，中国东晋书法家，有书圣之称。历任秘书郞、宁远将军、江州刺史。后为会稽内史，领右将军，人称“王右军”、“王会稽”。其子王献之书法亦佳，世人合称为“二王”。此后历代王氏家族书法人才辈出。东晋升平五年卒，葬于金庭瀑布山（又称紫藤山），其五世孙衡舍宅为金庭观，遗址犹存。', '303', '361', 10015, '2014-07-23 19:53:14'),
(10034, '王勃', '王勃，唐代诗人。汉族，字子安。绛州龙门（今山西河津）人。王勃也与杨炯、卢照邻、骆宾王齐名，齐称“初唐四杰”，其中王勃是“初唐四杰”之冠。', '649', '675', 10006, '2014-07-23 19:53:14'),
(10035, '杜甫', '杜甫，字子美，自号少陵野老，世称“杜工部”、“杜老”、“杜少陵”等，盛唐时期伟大的现实主义诗人。汉族，巩县（今河南巩义）人。杜甫曾祖父起由湖北襄阳迁居巩县。他忧国忧民，人格高尚，约1400余首诗被保留了下来，诗艺精湛，在中国古典诗歌中的影响非常深远，备受推崇。759-766年间曾居成都，后世有杜甫草堂纪念之。杜甫被世人尊为“诗圣”，其诗被称为“诗史”。杜甫与李白合称“李杜”，为了和李商隐与杜牧即“小李杜”区别开，杜甫与李白又合称“大李杜”。 ', '712', '770', 10006, '2014-07-23 19:53:14'),
(10036, '张若虚', '张若虚，唐代诗人。生于扬州（今属江苏），曾任兖州兵曹。事迹略见于《旧唐书·贺知章传》。中宗神龙（705～707）中，与贺知章、贺朝、万齐融、邢巨、包融俱以文词俊秀驰名于京都，与贺知章、张旭、包融并称“吴中四士”。玄宗开元时尚在世。张若虚的诗仅存二首于《全唐诗》中。其中《春江花月夜》是一篇脍炙人口的名作，它沿用陈隋乐府旧题，抒写真挚动人的离情别绪及富有哲理意味的人生感慨，语言清新优美，韵律宛转悠扬，洗去了宫体诗的浓脂艳粉，给人以澄澈空明、清丽自然的感觉。', '660', '720', 10006, '2014-07-23 19:53:14'),
(10037, '曹植', '曹植，字子建，沛国谯（今安徽省亳州市）人。三国曹魏著名文学家，建安文学代表人物。魏武帝曹操之子，魏文帝曹丕之弟，生前曾为陈王，去世后谥号“思”，因此又称陈思王。后人因他文学上的造诣而将他与曹操、曹丕合称为“三曹”，南朝宋文学家谢灵运更有“天下才有一石，曹子建独占八斗”的评价。王士祯尝论汉魏以来二千年间诗家堪称“仙才”者，曹植、李白、苏轼三人耳。', '192', '232', 10014, '2014-07-23 19:53:14'),
(10041, '李白', '李白，字太白，号青莲居士，唐朝诗人，有“诗仙”之称，伟大的浪漫主义诗人。汉族，出生于剑南道之绵州（今四川绵阳江油市青莲乡），一说生于西域碎叶城（今吉尔吉斯斯坦托克马克），5岁随父迁至剑南道之绵州（巴西郡）昌隆县（712年更名为昌明县），祖籍陇西郡成纪县（今甘肃天水市秦安县）。其父李客，育二子（伯禽、天然）一女（平阳）。存世诗文千余篇，代表作有《蜀道难》、《将进酒》等诗篇，有《李太白集》传世。762年病逝于安徽当涂，享年61岁。其墓在安徽当涂，四川江油、湖北安陆有纪念馆。', '701', '762', 10006, '2014-07-23 19:53:14'),
(10042, '骆宾王', '骆宾王，字观光，汉族，婺州义乌人（今中国浙江义乌）。唐初诗人，与王勃、杨炯、卢照邻合称“初唐四杰”。又与富嘉谟并称“富骆”。 唐龙朔初年，骆宾王担任道王李元庆的属官。后来相继担任武功主簿和明堂主簿。唐高宗仪凤四年（679年），升任中央政府的侍御史官职。曾经被人诬陷入狱，被赦免后出任地方官临海县丞，所以后人也称他骆临海。武则天光宅元年（684年），徐敬业起兵讨伐武则天，他作为秘书，起草了著名的《讨武瞾檄文》。', '619', '687', 10006, '2014-07-23 19:53:14'),
(10043, '李清照', '李清照，山东省济南章丘人，号易安居士。宋代女词人，婉约词派代表，有“千古第一才女”之称。早期生活优裕，与夫赵明诚共同致力于书画金石的搜集整理。金兵入据中原时，流寓南方，境遇孤苦。所作词，前期多写其悠闲生活，后期多悲叹身世，情调感伤。形式上善用白描手法，自辟途径，语言清丽。论词强调协律，崇尚典雅，提出词“别是一家”之说，反对以作诗文之法作词。能诗，留存不多，部分篇章感时咏史，情辞慷慨，与其词风不同。有《易安居士文集》《易安词》，已散佚。后人有《漱玉词》辑本。今有《李清照集校注》。', '1084', '1155', 10001, '2014-07-23 19:53:14'),
(10045, '冯延巳', '冯延巳，又名延嗣，字正中，五代广陵（今江苏省扬州市）人。在南唐做过宰相，生活过得很优裕、舒适。他的词多写闲情逸致辞，文人的气息很浓，对北宋初期的词人有比较大的影响。宋初《钓矶立谈》评其“学问渊博，文章颖发，辩说纵横”，其词集名《阳春集》。', '903', '960', 10020, '2014-07-23 19:53:14'),
(10046, '蒋坦', '蒋坦，字平伯，号蔼卿，浙江钱塘人。秀才出身，擅长书法。道光七年与青梅竹马的表妹关瑛（秋芙）订婚，道光二十三年成婚，长年居住于杭州西湖。咸丰年间秋芙病卒。咸丰十一年，太平军攻杭州，蒋坦避祸慈溪，投靠朋友王景曾，又回钱塘，不久饿死。著有《秋灯琐忆》、《息影庵初存诗》、《集外诗》、《微波集》等。徐世昌《晚晴蓉诗汇》曰：“后秋芙死，蔼卿为制《秋灯琐忆》，皆幽闺遗事”。秋芙被林语堂形容是中国古代最可爱的两个女性之一，另一个是沈复之妻芸娘。', '1818', '1863', 10008, '2014-07-23 19:53:14'),
(10047, '刘禹锡', '刘禹锡，字梦得，唐朝洛阳（今河南省洛阳）人唐朝文学家，哲学家，自称是汉中山靖王后裔，曾任监察御史，是王叔文政治改革集团的一员。唐代中晚期著名诗人，有“诗豪”之称。他的家庭是一个世代以儒学相传的书香门第。政治上主张革新，是王叔文派政治革新活动的中心人物之一。后来永贞革新失败被贬为朗州司马（今湖南常德）。据湖南常德历史学家、收藏家周新国先生考证刘禹锡被贬为朗州司马其间写了著名的“汉寿城春望”。', '772', '842', 10006, '2014-07-23 19:53:14'),
(10049, '姜夔', '姜夔，南宋文学家，音乐家。人品秀拔，体态清莹，气貌若不胜衣，望之若神仙中人。往来鄂、赣、皖、苏、浙间，与诗人词家杨万里、范成大、辛弃疾等交游。庆元中，曾上书乞正太常雅乐，他少年孤贫，屡试不第，终生未仕，一生转徙江湖，靠卖字和朋友接济为生。他多才多艺，精通音律，能自度曲，其词格律严密。其作品素以空灵含蓄著称，有《白石道人歌曲》等。姜夔对诗词、散文、书法、音乐，无不精善，是继苏轼之后又一难得的艺术全才。', '1154', '1221', 10001, '2014-07-23 19:53:14'),
(10050, '黄巢', '黄巢，曹州冤句（今山东菏泽西南）人，唐末时人。初为盐帮首领，售私盐为业，后成农民军首领，曾自立为帝，尊号为承天应运启圣睿文宣武皇帝，国号大齐，史称黄巢之乱。黄巢退败时死于部下之手。野史有称“黄巢杀人八百万”恶名。', '835', '884', 10006, '2014-07-23 19:53:14'),
(10051, '王昌龄', '王昌龄，字少伯，山西太原人。盛唐著名边塞诗人，后人誉为“七绝圣手”。早年贫贱，困于农耕，年近不惑，始中进士。初任秘书省校书郎，又中博学宏辞，授汜水尉，因事贬岭南。与李白、高适、王维、王之涣、岑参等交厚。开元末返长安，改授江宁丞。被谤谪龙标尉。安史乱起，为刺史闾丘所杀。其诗以七绝见长，尤以登第之前赴西北边塞所作边塞诗最著，有“诗家夫子王江宁”之誉。 ', '690', '756', 10006, '2014-07-23 19:53:14'),
(10052, '树下野狐', '树下野狐，原名胡庚，福建人，毕业于北京大学。2001年7月开始创作的《搜神记》，开创了中国新神话主义的东方奇幻风格，被誉为“本土奇幻扛旗人”、“北大蒲松龄”、“当代新神话主义浪潮的领军人物”。', '1982', '', 10022, '2014-07-23 19:53:14'),
(10053, '张志和', '张志和，字子同，初名龟龄，汉族，婺州（今浙江金华）人，自号“烟波钓徒”，又号“玄真子”。唐代著名道士、词人和诗人。十六岁参加科举，以明经擢第，授左金吾卫录事参军，唐肃宗赐名为“志和”。因事获罪贬南浦尉，不久赦还。自此看破红尘，浪迹江湖，隐居祁门赤山镇。其兄张鹤龄担心他遁世不归，在越州（今绍兴市）城东筑茅屋让他居住。史载唐肃宗曾赐他奴婢各一人，张志和让他们结婚，取名渔童和樵青。著有《玄真子》集。', '730', '810', 10006, '2014-07-23 19:53:14'),
(10054, '张孝祥', '张孝祥，字安国，别号于湖居士，汉族，历阳乌江（今安徽省和县）人，生于明州鄞县（今浙江宁波）。南宋著名词人，书法家。父亲张祁，任直秘阁、淮南转运判官。少年时阖家迁居芜湖（今安徽省芜湖市）。绍兴二十四年（1154）廷试，高宗（赵构）亲擢为进士第一。授承事郎，签书镇东军节度判官。由于上书为岳飞辩冤，为当时权相秦桧所忌，诬陷其父张祁有反谋，并将其父下狱。次年秦桧死，授秘书省正字。历任秘书郎，著作郎，集英殿修撰，中书舍人等职。1163年，张浚出兵北伐，被任为建康留守。此外还出任过抚州，平江，静江，潭州等地的地方长官。乾道五年（1169年），以显谟阁直学士致仕。是年夏于芜湖病死，葬南京江浦老山。年三十八岁。有《于湖居士文集》、《于湖词》传世。《全宋词》辑录其223首词。', '1132', '1170', 10001, '2014-08-20 21:17:22'),
(10055, '柳宗元', '柳宗元，字子厚，唐代河东郡（今山西永济县）人，著名杰出诗人、哲学家、儒学家乃至成就卓著的政治家，唐宋八大家之一。著名作品有《永州八记》等六百多篇文章，经后人辑为三十卷，名为《柳河东集》。因为他是河东人，人称柳河东，又因终于柳州刺史任上，又称柳柳州。柳宗元与韩愈同为中唐古文运动的领导人物，并称“韩柳”。', '773', '819', 10006, '2014-07-23 19:53:14'),
(10059, '杨慎', '杨慎，明代文学家，明代三大才子之一。字用修，号升庵，后因流放滇南，故自称博南山人、金马碧鸡老兵。杨廷和之子，汉族，四川新都（今成都市新都区）人，祖籍庐陵。正德六年状元，官翰林院修撰，豫修武宗实录，禀性刚直，每事必直书。武宗微行出居庸关，上疏抗谏。世宗继位，任经筵讲官。嘉靖三年，因“大礼议”受廷杖，谪戍终老于云南永昌卫。终明一世记诵之博，著述之富，慎可推为第一。其诗虽不专主盛唐，仍有拟右倾向。贬谪以后，特多感愤。又能文、词及散曲，论古考证之作范围颇广。著作达百余种。后人辑为《升庵集》。', '1488', '1559', 10007, '2014-07-23 19:53:14'),
(10060, '柳永', '柳永，北宋著名词人，婉约派最具代表性的人物。汉族，崇安（今福建武夷山）人，原名三变，字景庄，后改名永，字耆卿，排行第七，又称柳七。宋仁宗朝进士，官至屯田员外郎，故世称柳屯田。他自称“奉旨填词柳三变”，以毕生精力作词，并以“白衣卿相”自诩。其词多描绘城市风光和歌妓生活，尤长于抒写羁旅行役之情，创作慢词独多。铺叙刻画，情景交融，语言通俗，音律谐婉，在当时流传极其广泛，人称“凡有井水饮处，皆能歌柳词”，对宋词的发展有重大影响。', '987', '1053', 10001, '2014-07-23 19:53:14'),
(10062, '商汤', '商汤，子姓，名履，庙号太祖，为商太祖，河南商丘人。商朝的创建者，前1617~前1588年在位，在位30年，其中17年为夏朝商国诸侯，13年为商朝国王。今人多称商汤，又称武汤、天乙、成汤、成唐，甲骨文称唐、大乙，又称高祖乙，商人部落首领。', '?', '-1588', 10019, '2014-12-01 00:47:43'),
(10064, '庄子', '庄子，姓庄，名周，战国时期思想家和哲学家、思想家、文学家，宋国蒙（今河南商丘民权县），道家学说的创始人之一。庄子祖上系出楚国公族，后因吴起变法楚国发生内乱，先人避夷宗之罪迁至宋国蒙地。庄子生平只做过地方漆园吏，因崇尚自由而不应同宗楚威王之聘。老子思想的继承和发展者。后世将他与老子并称为“老庄”。他们的哲学思想体系，被思想学术界尊为“老庄哲学”。代表作品为《庄子》，名篇有《逍遥游》、《齐物论》等。', '-369', '-286', 10005, '2014-08-21 08:02:31'),
(10065, '司马迁', '司马迁，字子长，西汉夏阳（今陕西韩城，一说山西河津）人，中国古代伟大的史学家、文学家，被后人尊为“史圣”。他最大的贡献是创作了中国第一部纪传体通史《史记》（原名《太史公书》）。《史记》记载了从上古传说中的黄帝时期，到汉武帝元狩元年，长达3000多年的历史。司马迁以其“究天人之际，通古今之变，成一家之言”的史识完成的史学巨著《史记》，是“二十五史”之首，被鲁迅誉为“史家之绝唱，无韵之《离骚》”。', '-145', '-87', 10013, '2014-08-21 08:03:04'),
(10066, '文天祥', '字履善，一字宋瑞，号文山。吉州庐陵（今江西吉安）人。1256年（理宗宝佑四年）中榜上第一名。在贾似道当权时受排挤。1275年，元军进攻临安，宋朝廷下诏征勤王兵。任赣州知州的文天祥组成万人的义军，北上守卫。1276年任右丞相，被派往元军营谈判，被扣留。押解中在镇江逃出，得到人民的帮助，流亡至通州（今江苏南通），从海道到福建，与张世杰、陆秀夫等在福州拥生赵呈为帝，坚持抗元。1277年进兵江西收复了几个县城，使抗元形势大力好转。但不久为元军所败，退入广东。1278年在五坡岭（今广东海丰北）被俘。元将张弘范叫他写信招张世杰投降，遭拒绝，书《过零丁洋》诗以明志。1279年被送到大都（今北京），囚3年，途经威胁利诱，始终不屈。1283年1月9日（至元十九年十二月初九日）在柴市被害，从容就义。', '1236', '1283', 10001, '2014-07-23 19:53:14'),
(10067, '王安石', '王安石，字介甫，号半山，谥文，封荆国公。世人又称王荆公。北宋抚州临川人（今临川区邓家巷人），中国历史上杰出的政治家、思想家、学者、诗人、文学家、改革家，唐宋八大家之一。北宋丞相、新党领袖。欧阳修称赞王安石：“翰林风月三千首，吏部文章二百年。老去自怜心尚在，后来谁与子争先。”传世文集有《王临川集》、《临川集拾遗》等。其诗文各体兼擅，词虽不多，但亦擅长，且有名作《桂枝香》等。', '1021', '1086', 10001, '2014-08-20 23:40:40'),
(10068, '张继', '张继，字懿孙，汉族，襄州人（今湖北襄阳人）。唐代诗人，他的生平不甚可知。据诸家记录，仅知他是天宝十二年（公元七五三年）的进士。大历中，以检校祠部员外郎为洪州（今江西南昌市）盐铁判官。他的诗爽朗激越，不事雕琢，比兴幽深，事理双切，对后世颇有影响。但可惜流传下来的不到50首，最著名的诗是《枫桥夜泊》。', '715', '779', 10006, '2014-07-23 19:53:14'),
(10069, '温庭筠', '温庭筠，唐代诗人、词人。本名岐，字飞卿，太原祁（今山西祁县东南）人。富有天才，文思敏捷，每入试，押官韵，八叉手而成八韵，所以也有“温八叉”之称。然恃才不羁，又好讥刺权贵，多犯忌讳，取憎于时，故屡举进士不第，长被贬抑，终生不得志。官终国子助教。精通音律。工诗，与李商隐齐名，时称“温李”。其诗辞藻华丽，秾艳精致，内容多写闺情。其词艺术成就在晚唐诸词人之上，为“花间派”首要词人，对词的发展影响较大。在词史上，与韦庄齐名，并称“温韦”。存词七十余首。后人辑有《温飞卿集》及《金奁集》。', '812', '866', 10006, '2014-07-23 19:53:14'),
(10070, '王国维', '王国维，字伯隅、静安，号观堂、永观，汉族，浙江海宁盐官镇人。清末秀才。我国近现代在文学、美学、史学、哲学、古文字学、考古学等各方面成就卓著的学术巨子，国学大师。', '1877', '1927', 10008, '2014-07-23 19:53:14'),
(10071, '蒋捷', '蒋捷，字胜欲，号竹山，宋末元初阳羡（今江苏宜兴）人。先世为宜兴巨族，咸淳十年（1274）进士。南宋亡，深怀亡国之痛，隐居不仕，人称“竹山先生”、“樱桃进士”，其气节为时人所重。长于词，与周密、王沂孙、张炎并称“宋末四大家”。其词多抒发故国之思、山河之恸 ，风格多样，而以悲凉清俊、萧寥疏爽为主。尤以造语奇巧之作，在宋季词坛上独标一格，有《竹山词》1卷，收入毛晋《宋六十名家词》本、《疆村丛书》本；又《竹山词》2卷，收入涉园景宋元明词续刊本。', '?', '?', 10001, '2014-07-23 19:53:14'),
(10072, '黄景仁', '黄景仁，清代诗人。字汉镛，一字仲则，号鹿菲子，阳湖（今江苏省常州市）人。四岁而孤，家境清贫，少年时即负诗名，为谋生计，曾四方奔波。一生怀才不遇，穷困潦倒，后授县丞，未及补官即在贫病交加中客死他乡，年仅35岁。诗负盛名，为“毗陵七子”之一。诗学李白，所作多抒发穷愁不遇、寂寞凄怆之情怀，也有愤世嫉俗的篇章。七言诗极有特色。亦能词。著有《两当轩全集》。', '1749', '1783', 10008, '2014-07-23 19:53:14'),
(10073, '刘过', '刘过，南宋文学家，字改之，号龙洲道人。湖北襄阳人，后移居吉州太和（今江西泰和县）人，长于庐陵（今江西吉安）。四次应举不中，流落江湖间，布衣终身。曾为陆游、辛弃疾所赏，亦与陈亮、岳珂友善。词风与辛弃疾相近，抒发抗金抱负狂逸俊致，与刘克庄、刘辰翁享有“辛派三刘”之誉，又与刘仙伦合称为“庐陵二布衣”。有《龙洲集》、《龙洲词》。', '1154', '1206', 10001, '2014-07-23 19:53:14'),
(10074, '元好问', '元好问，字裕之，号遗山，太原秀容（今山西忻州）人；系出北魏鲜卑族拓跋氏，元好问过继叔父元格；七岁能诗，十四岁从学郝天挺，六载而业成；兴定五年（1221）进士，不就选；正大元年（1224 ），中博学宏词科，授儒林郎，充国史院编修，历镇平、南阳、内乡县令。八年（1231）秋，受诏入都，除尚书省掾、左司都事，转员外郎；金亡不仕，元宪宗七年卒于获鹿寓舍；工诗文，在金元之际颇负重望；诗词风格沉郁，并多伤时感事之作。其《论诗》绝句三十首在中国文学批评史上颇有地位；作有《遗山集》又名《遗山先生文集》，编有《中州集》。', '1190', '1257', 10026, '2015-02-13 12:03:50'),
(10075, '王维', '王维，字摩诘，汉族，河东蒲州（今山西运城）人，祖籍山西祁县，唐朝诗人，有“诗佛”之称。开元九年（721年）中进士，任太乐丞。王维是盛唐诗人的代表，今存诗400余首，重要诗作有“相思”“山居秋暝”等。王维精通佛学，受禅宗影响很大。佛教有一部《维摩诘经》，是王维名和字的由来。王维诗书画都很有名，非常多才多艺，音乐也很精通。与孟浩然合称“王孟”。', '701', '761', 10006, '2014-07-23 19:53:14'),
(10076, '刘基', '刘基，字伯温，谥曰文成，汉族，青田县南田乡（今属浙江省文成县）人，故时人称他刘青田，明洪武三年（1370）封诚意伯，人们又称他刘诚意。武宗正德九年追赠太师，谥文成，后人又称他刘文成、文成公。元末明初杰出的战略家、政治家及文学家，通经史、晓天文、精兵法。他以辅佐朱元璋完成帝业、开创明朝并尽力保持国家的安定，因而驰名天下，被后人比作为诸葛武侯。朱元璋多次称刘基为：“吾之子房也。”在文学史上，刘基与宋濂、高启并称“明初诗文三大家”。', '1311', '1375', 10007, '2014-07-23 19:53:14'),
(10077, '无名氏', '周朝佚名作者的统称。', '?', '?', 10005, '2014-07-23 19:53:14'),
(10078, '黄公绍', '黄公绍，宋元之际邵武（今属福建）人，字直翁。咸淳进士。入元不仕，隐居樵溪。著《古今韵会》、以《说文》为本，参考宋元以前字书、韵书，集字书训诂之大成，原书已佚，其同时人熊忠所编《古今韵会举要》中，略能见其大概。另有《在轩集》。', '?', '?', 10001, '2014-07-23 19:53:14'),
(10079, '晏几道', '晏几道，字叔原，号小山，著名词人，抚州临川文港沙河（今属江西南昌进贤县）人，晏殊第七子。历任颍昌府许田镇监、乾宁军通判、开封府判官等。性孤傲，晚年家境中落。词风哀感缠绵、清壮顿挫。一般讲到北宋词人时，称晏殊为大晏，称晏几道为小晏。《雪浪斋日记》云：“晏叔原工小词，不愧六朝宫掖体。”《鹧鸪天》中“舞低杨柳楼心月，歌尽桃花扇底风。”两句受人赞赏。', '1030', '1106', 10001, '2014-07-23 19:53:14'),
(10080, '龚自珍', '龚自珍，清代思想家、文学家及改良主义的先驱者。27岁中举人，38岁中进士。曾任内阁中书、宗人府主事和礼部主事等官职。主张革除弊政，抵制外国侵略，曾全力支持林则徐禁除鸦片。48岁辞官南归，次年暴卒于江苏丹阳云阳书院。他的诗文主张“更法”、“改图”，揭露清统治者的腐朽，洋溢着爱国热情，被柳亚子誉为“三百年来第一流”。著有《定庵文集》，留存文章300余篇，诗词近800首，今人辑为《龚自珍全集》。著名诗作《己亥杂诗》共315首。', '1792', '1841', 10008, '2014-07-23 19:53:14'),
(10081, '林景熙', '林景熙，字德暘，一作德阳，号霁山。温州平阳（今属浙江）人。南宋末期爱国诗人。咸淳七年（公元1271年），由上舍生释褐成进士，历任泉州教授，礼部架阁，进阶从政郎。宋亡后不仕，隐居于平阳县城白石巷。林景熙等曾冒死捡拾帝骨葬于兰亭附近。他教授生徒，从事著作，漫游江浙，是雄踞宋元之际诗坛、创作成绩卓著、最富代表性的作家，也是温州历史上成就最高的诗人。卒葬家乡青芝山。著作编为《霁山集》。', '1242', '1310', 10001, '2014-07-23 19:53:14'),
(10082, '元稹', '元稹，字微之，别字威明，唐洛阳人（今河南洛阳）。父元宽，母郑氏。为北魏宗室鲜卑族拓跋部后裔，是什翼犍之十四世孙。早年和白居易共同提倡“新乐府”。世人常把他和白居易并称“元白”。', '779', '831', 10006, '2014-07-23 19:53:14'),
(10083, '崔护', '崔护，唐代诗人。字殷功，唐代博陵（今河北定州市）人。公元796年（贞元十二年）登第（进士及第）。公元829年（大和三年）为京兆尹，同年为御史大夫、岭南节度使。终岭南节度使。其诗诗风精练婉丽，语极清新。《全唐诗》存诗六首，皆是佳作，尤以《题都城南庄》流传最广，脍炙人口，有目共赏。该诗以“人面桃花，物是人非”这样一个看似简单的人生经历道出了千万人都似曾有过的共同生活体验，为诗人赢得了不朽的诗名。', '?', '?', 10006, '2014-07-23 19:53:14'),
(10084, '舒亶', '舒亶，字信道，号懒堂，慈溪（今属浙江）人。治平二年（1065）试礼部第一，即状元（进士及第），授临海尉。神宗时，除神官院主簿，迁秦凤路提刑，提举两浙常平。后任监察御史里行，与李定同劾苏轼，是为「乌台诗案」。进知杂御史、判司农寺，拜给事中，权直学士院，后为御史中丞。崇宁元年（1102）知南康军，京以开边功，由直龙图阁进待制，翌年卒，年六十三。《宋史》、《东都事略》有传。今存赵万里辑《舒学士词》一卷，存词50首。', '1041', '1103', 10001, '2014-07-23 19:53:14'),
(10085, '秦观', '秦观，字少游、太虚，号淮海居士，扬州高邮（今属江苏）人。北宋词人，“苏门四学士”之一。', '1049', '1100', 10001, '2014-07-23 19:53:14'),
(10086, '黄庭坚', '黄庭坚，字鲁直，自号山谷道人，晚号涪翁，又称豫章黄先生，汉族，洪州分宁（今江西修水）人。北宋诗人、词人、书法家，为盛极一时的江西诗派开山之祖，而且，他跟杜甫、陈师道和陈与义素有“一祖三宗”（黄为其中一宗）之称。英宗治平四年（1067）进士。历官叶县尉、北京国子监教授、校书郎、著作佐郎、秘书丞、涪州别驾、黔州安置等。诗歌方面，他与苏轼并称为“苏黄”；书法方面，他则与苏轼、米芾、蔡襄并称为“宋代四大家”；词作方面，虽曾与秦观并称“秦黄”，但黄氏的词作成就却远逊于秦氏。', '1045', '1105', 10001, '2014-10-17 23:15:34'),
(10087, '周敦颐', '周敦颐，宋营道楼田堡（今湖南道县）人，北宋著名哲学家，是学术界公认的理学派开山鼻祖。“两汉而下，儒学几至大坏。千有余载，至宋中叶，周敦颐出于舂陵，乃得圣贤不传之学，作《太极图说》、《通书》，推明阴阳五行之理，明于天而性于人者，了若指掌。”《宋史·道学传》将周子创立理学学派提高到了极高的地位。', '1017', '1073', 10001, '2014-07-23 19:53:14'),
(10088, '苏味道', '苏味道，唐代政治家、文学家。赵州栾城（今河北石家庄市栾城县）人，少有才华，20岁举进士 ，累迁咸阳尉。武则天时居相位数年，苟合取容，处事依违两可，时称“苏模棱”。因阿附张易之，中宗时贬郿州刺史，死于任所。与杜审言、崔融、李峤并称为文章四友，与李峤并称苏李。对唐代律诗发展有推动作用，诗多应制之作，浮艳雍容。但《正月十五夜》（一作《上元》）咏长安元宵夜花灯盛况，为传世之作。原有集，今佚。《全唐诗》录其诗16首。苏味道死后葬今栾城苏邱村，其一子留四川眉山，宋代“三苏”为其后裔。', '648', '705', 10006, '2014-07-23 19:53:14'),
(10090, '张养浩', '张养浩，字希孟，号云庄，济南（今属山东）人，唐朝名相张九龄的弟弟张九皋的第23代孙。元代散曲家。少年知名，19岁被荐为东平学正，历官堂邑县尹、监察御史、翰林学士、礼部尚书、参议中书省事等官职。在任监察御史时，因批评时政而为权贵所忌，被免官。后复官至礼部尚书，参议中书省事。后因其父年迈，无人照顾，于英宗至治二年（1322年）辞官家居，此后屡召不赴。文宗天历二年（1329年），关中大旱，特拜陕西行台中丞，办理赈灾，见饥民困苦万分，难以周济，竟为之痛哭，遂“散其家之所有” ，“登车就道”，不久便积劳成疾病卒。追封滨国公，谥文忠。', '1269', '1329', 10009, '2014-07-23 19:53:14'),
(10091, '马致远', '马致远，字千里，号东篱，（一说字致远，晚号“东篱”）。 汉族，大都（今北京）人，另一说（马致远是河北省东光县马祠堂村人，号东篱，以示效陶渊明之志）。他的年辈晚于关汉卿、白朴等人，生年当在至元（始于1264）之前，卒年当在至治改元到泰定元年（1321—1324）之间，与关汉卿、郑光祖、白朴并称“元曲四大家”，是我国元代时著名大戏剧家、散曲家。', '1250', '1321', 10009, '2014-07-23 19:53:14'),
(10092, '荆轲', '荆轲，战国末期卫国人，中国战国时期著名刺客，也称庆卿、荆卿、庆轲，是春秋时期齐国大夫庆封的后代。受燕太子丹之托入刺秦王，因为被夏无且（jū）的药囊击中，被秦王拔剑所杀，秦王杀他时连刺八下才停手，行刺失败被杀。', '?', '-227', 10005, '2014-07-23 19:53:14'),
(10093, '崔颢', '崔颢，唐代诗人，汴州（今河南开封）人。开元十一年（723年）中进士，官司勋员外郎。现存诗仅四十多首，最有名的一首莫过于《黄鹤楼》。少年时作的诗多写闺情，流于浮艳，后历边塞，诗风变得雄浑奔放。他四处游历。崔颢吟诗甚勤，其友人笑他吟诗吟得人也瘦（非子病如此，乃苦吟诗瘦耳）。明人辑有《崔颢集》。 李白叹云：“眼前有景道不得，崔颢题诗在上头”', '704', '754', 10006, '2014-07-23 19:53:14'),
(10094, '王之涣', '王之涣，是盛唐时期的诗人，字季凌，汉族，并州（山西太原）人。祖籍晋阳（今山西太原），其高祖迁今山西新绛县。豪放不羁，常击剑悲歌，其诗多被当时乐工制曲歌唱。名动一时，常与高适、王昌龄等相唱和，以善于描写边塞风光著称。代表作有《登鹳雀楼》、《凉州词》等。“白日依山尽，黄河入海流。欲穷千里目，更上一层楼。”更是千古绝唱。', '688', '742', 10006, '2014-08-20 23:36:03'),
(10095, '张九龄', '张九龄，唐开元尚书丞相，诗人。字子寿，一名博物，汉族，韶州曲江（今广东韶关市）人。长安年间进士。官至中书侍郎同中书门下平章事。后罢相，为荆州长史。诗风清淡。有《曲江集》。他是一位有胆识、有远见的著名政治家、文学家、诗人、名相。他忠耿尽职，秉公守则，直言敢谏，选贤任能，不徇私枉法，不趋炎附势，敢与恶势力作斗争，为“开元之治”作出了积极贡献。他的五言古诗，以素练质朴的语言，寄托深远的人生慨望，对扫除唐初所沿习的六朝绮靡诗风，贡献尤大，被誉为“岭南第一人”。', '678', '740', 10006, '2014-10-17 23:14:23'),
(10096, '岑参', '岑参（cén shēn），唐代诗人，原籍南阳（今属河南新野），迁居江陵（今属湖北）。汉族，荆州江陵（湖北江陵）人，去世之时56岁，是唐代著名的边塞诗人。其诗歌富有浪漫主义的特色，气势雄伟，想象丰富，色彩瑰丽，热情奔放，尤其擅长七言歌行。', '715', '770', 10006, '2014-07-23 19:53:14'),
(10097, '叶梦得', '叶梦得，宋代词人，字少蕴，苏州吴县人。绍圣四年（1097）登进士第，历任翰林学士、户部尚书、江东安抚大使等官职。晚年隐居湖州弁山玲珑山石林，故号石林居士，所著诗文多以石林为名，如《石林燕语》、《石林词》、《石林诗话》等。绍兴十八年卒，年七十二。死后追赠检校少保。 在北宋末年到南宋前半期的词风变异过程中，叶梦得是起到先导和枢纽作用的重要词人。作为南渡词人中年辈较长的一位，叶梦得开拓了南宋前半期以"气"入词的词坛新路。叶词中的气主要表现在英雄气、狂气、逸气三方面。', '1077', '1148', 10001, '2014-10-17 23:16:04'),
(10098, '贺铸', '贺铸，北宋词人。字方回，号庆湖遗老。汉族，卫州（今河南卫辉）人。宋太祖贺皇后族孙，所娶亦宗室之女。自称远祖本居山阴，是唐贺知章后裔，以知章居庆湖（即镜湖），故自号庆湖遗老。', '1052', '1125', 10001, '2014-07-23 19:53:14');
INSERT INTO `author` (`id`, `name`, `intro`, `birth_year`, `death_year`, `dynasty_id`, `updated_at`) VALUES
(10099, '袁枚', '袁枚，清代诗人、散文家。字子才，号简斋，晚年自号仓山居士、随园主人、随园老人。汉族，钱塘（今浙江杭州）人。乾隆四年进士，历任溧水、江宁等县知县，有政绩，四十岁即告归。在江宁小仓山下筑随园，吟咏其中。广收诗弟子，女弟子尤众。袁枚是乾嘉时期代表诗人之一，与赵翼、蒋士铨合称“乾隆三大家”。', '1716', '1797', 10008, '2014-07-23 19:53:14'),
(10100, '无名氏', '汉代佚名作者的统称。', '?', '?', 10013, '2014-07-23 19:53:14'),
(10101, '无名氏', '南北朝佚名作者的统称。', '?', '?', 10016, '2014-07-23 19:53:14'),
(10102, '陈与义', '陈与义，字去非，号简斋，汉族，其先祖居京兆，自曾祖陈希亮迁居洛阳，故为宋代河南洛阳人（现在属河南）。生于宋哲宗元祐五年（1090年），卒于南宋宋高宗绍兴八年（1138年）。北宋末，南宋初年的杰出诗人，同时也工于填词。其词存于今者虽仅十余首，却别具风格，尤近于苏东坡，语意超绝，笔力横空，疏朗明快，自然浑成，著有《简斋集》。', '1090', '1138', 10001, '2014-10-17 23:16:35'),
(10103, '唐寅', '唐寅（yín），字伯虎，一字子畏，号六如居士、桃花庵主、鲁国唐生、逃禅仙吏等，据传于明宪宗成化六年庚寅年寅月寅日寅时生，故名唐寅。汉族，吴县（今江苏苏州）人。他玩世不恭而又才气横溢，诗文擅名，与祝允明、文征明、徐祯卿并称“江南四才子”，画名更著，与沈周、文征明、仇英并称“吴门四家”。', '1470', '1523', 10007, '2014-07-23 19:53:14'),
(10104, '王翰', '王翰，唐代边塞诗人。字子羽，并州晋阳（今山西太原市）人。王翰家中富裕，性格豪放不羁，喜饮酒，于唐睿宗景云元年进士，并州长史张惠贞奇其才，举直言极谏、超拔群类等制科，调王翰昌乐尉。开元九年（721年），张说入朝为相，召王翰为秘书正字，又擢驾部员外郎。“枥多名马，家有妓乐”，“发言立意，自比王侯。颐指侪类，人多嫉之。”出为汝州长史，改为仙州别驾，仍然“日聚英豪，从禽击鼓，恣为欢赏”。后被贬为道州司马，卒于前往道州途中。其作品《凉州词》为古今之绝唱。', '?', '687', 10006, '2014-07-23 19:53:14'),
(10105, '陈陶', '陈陶，字嵩伯，自号三教布衣，岭南人。诗人早年游学长安，善天文历象，尤工诗。举进士不第，遂恣游名山。唐宣宋大中时，隐居洪州西山，后不知所终。有诗十卷，已散佚，后人辑有《陈嵩伯诗集》一卷。其《陇西行》四首之二：“誓扫匈奴不顾身，五千貂锦丧胡尘。可怜无定河边骨，犹是春闺梦里人。”把残酷现实与少妇美梦交替在一起，造成强烈的艺术效果，至今仍脍炙人口。', '812', '885', 10006, '2014-07-23 19:53:14'),
(10106, '宋江', '宋江，北宋宣和年间民变首领，后来投降宋朝。此事成为后来章回小说《水浒传》的原形，广为人知。而宋江在小说中则是梁山泊起义军之首，乃天魁星也。原为山东郓城县一刀笔小吏，字公明，绰号呼保义，又号及时雨，与托塔天王晁盖为生死之交。', '?', '?', 10001, '2014-07-23 19:53:14'),
(10108, '李贺', '李贺，唐代著名诗人，汉族，河南福昌人。字长吉，世称李长吉、鬼才、诗鬼等，与李白、李商隐三人并称唐代“三李”。祖籍陇西，生于福昌县昌谷（今河南洛阳宜阳县）。一生愁苦多病，仅做过3年从九品微官奉礼郎，因病27岁卒。李贺是中唐浪漫主义诗人的代表，又是中唐到晚唐诗风转变期的重要人物。', '790', '816', 10006, '2014-07-23 19:53:14'),
(10109, '杜牧', '杜牧，字牧之，号樊川居士，汉族，京兆万年（今陕西西安）人。 杜牧是唐代杰出的诗人、散文家，是宰相杜佑之孙，杜从郁之子。唐文宗大和二年26岁中进士，授弘文馆校书郎。后赴江西观察使幕，转淮南节度使幕，又入观察使幕，理人国史馆修撰，膳部、比部、司勋员外郎，黄州、池州、睦州刺史等职，最终官居中书舍人。因晚年居长安南樊川别墅，故后世称“杜樊川”，著有《樊川文集》。杜牧的诗歌以七言绝句著称，内容以咏史抒怀为主，其诗英发俊爽，多切经世之物，在晚唐成就颇高。杜牧人称“小杜”，以别于杜甫，"大杜“。与李商隐并称“小李杜”。', '803', '852', 10006, '2014-10-17 23:15:13'),
(10110, '寒山', '寒山（生卒年不详），字、号均不详，唐代首都长安（今陕西西安）人。出身于官宦人家，多次投考不第，被迫出家，三十岁后隐居于浙东天台山，享年一百多岁。严振非《寒山子身世考》中更以《北史》、《隋书》等大量史料与寒山诗相印证，指出寒山乃为隋皇室后裔杨瓒之子杨温，因遭皇室内的妒忌与排挤及佛教思想影响而遁入空门，隐于天台山寒岩。这位富有神话色彩的唐代诗人，曾经一度被世人冷落，然而随着二十世纪的到来，其诗却越来越多地被世人接受并广泛流传。正如其诗所写：“有人笑我诗，我诗合典雅。不烦郑氏笺，岂用毛公解。”', '?', '?', 10006, '2014-10-12 10:29:11'),
(10111, '周邦彦', '周邦彦，北宋词人。字美成，号清真居士，汉族，钱塘（今浙江省杭州市）人。官历太学正、庐州教授、知溧水县等。少年时期个性比较疏散，但相当喜欢读书，宋神宗时，写《汴都赋》赞扬新法。徽宗时为徽猷阁待制，提举大晟府（最高音乐机关）。精通音律，曾创作不少新词调。作品多写闺情、羁旅，也有咏物之作。格律谨严，语言曲丽精雅，长调尤善铺叙。为后来格律派词人所宗。作品在婉约词人中长期被尊为“正宗”。旧时词论称他为“词家之冠”或“词中老杜”。有《清真居士集》，已佚，今存《片玉集》。', '1056', '1121', 10001, '2014-10-12 10:49:45'),
(10112, '李益', '李益， 唐代诗人，字君虞，陇西姑臧（今甘肃武威）人，后迁河南洛阳。大历四年（769）进士，初任郑县尉，久不得升迁，建中四年（783）登书判拔萃科。因仕途失意，后弃官在燕赵一带漫游。以边塞诗作名世，擅长绝句，尤其工于七绝。', '750', '830', 10006, '2014-10-12 10:57:18'),
(10113, '刘长卿', '刘长卿，字文房，汉族，宣城（今属安徽）人，唐代诗人。后迁居洛阳，河间（今属河北）为其郡望。玄宗天宝年间进士。肃宗至德中官监察御史，后为长洲县尉，因事下狱，贬南巴尉。代宗大历中任转运使判官，知淮西、鄂岳转运留后，又被诬再贬睦州司马。德宗建中年间，官终随州刺史，世称刘随州。', '726', '786', 10006, '2014-10-12 15:20:57'),
(10114, '钱起', '钱起，字仲文，吴兴（今属浙江）人。天宝进士，曾任考功郎中，故世称钱考功。代宗大历中为翰林学士。他是大历十才子之一，也是其中杰出者。又与郎士元齐名，当时称为“前有沈宋，后有钱郎”。', '?', '780', 10006, '2014-10-12 15:49:14'),
(10115, '韦应物', '韦应物，中国唐代诗人。汉族，长安(今陕西西安)人。今传有10卷本《韦江州集》、两卷本《韦苏州诗集》、10卷本《韦苏州集》。散文仅存一篇。因出任过苏州刺史，世称“韦苏州”。诗风恬淡高远，以善于写景和描写隐逸生活著称。', '737', '792', 10006, '2014-10-16 10:34:17'),
(10116, '王湾', '王湾，号为德，唐代诗人，洛阳（今河南洛阳）人。他的诗现存10首。作为开元初年的北方诗人，往来于吴楚间，为江南清丽山水所倾倒，并受到当时吴中诗人清秀诗风的影响，写下了一些歌咏江南山水的作品，《次北固山下》就是其中最为著名的一篇。尤其其中“海日生残夜，江春入旧年”两句，得到当时的宰相张说的极度赞赏，并亲自书写悬挂于宰相政事堂上，让文人学士作为学习的典范。由此，这两句诗中表现的那种壮阔高朗的境象便对盛唐诗坛产生了重要的影响。直到唐末诗人郑谷还说“何如海日生残夜，江春入旧年。”', '693', '751', 10006, '2014-10-16 11:06:31'),
(10117, '常建', '唐代诗人，字号不详，祖籍邢州（今邢台），开元十五年与王昌龄同榜进士，长仕宦不得意，来往山水名胜，过着一个很长时期的漫游生活。后移家隐居鄂渚。大历中，曾任盱眙尉。常建的诗现存不多，其中《题破山寺后禅院》较为著名。', '?', '?', 10006, '2014-11-02 10:03:53'),
(10118, '鲁迅', '鲁迅，原名周树人，字豫才，后改为豫亭，浙江绍兴会稽县人，中国现代伟大的无产阶级文学家、思想家和革命家。世界十大文豪之一，新文化运动的领导人、文化运动的支持者，是中国无产阶级文学的奠基人，也是中国现代文学的一面旗帜。', '1881', '1936', 10022, '2014-11-10 20:38:29'),
(10119, '谭嗣同', '谭嗣同，湖南浏阳人，是中国近代资产阶级著名的政治家、思想家，维新志士。少时师从欧阳中鹄，后加入维新派。他主张中国要强盛，只有发展民族工商业，学习西方资产阶级的政治制度。公开提出废科举、兴学校、开矿藏、修铁路、办工厂、改官制等变法维新的主张。写文章抨击清政府的卖国投降政策。1898年参加领导戊戌变法，失败后被杀，年仅三十四岁，与杨锐，刘光第，林旭，杨深秀和康广仁并称为“戊戌六君子”。代表作品有《仁学》、《狱中题壁》、《寥天一阁文》、《莽苍苍斋诗》、《远遗堂集外文》等。', '1864', '1898', 10008, '2014-11-10 20:45:59'),
(10120, '丘为', '丘为，生卒年不详，字号不详，唐代诗人，苏州嘉兴（今浙江嘉兴市）人。事继母孝，常有灵芝生堂下。累官太子右庶子。致仕，给俸禄之半以终身。年八十余，母尚无恙。及居忧，观察使韩滉以致仕官给禄，所以惠养老臣，不可在丧而异，惟罢春秋羊酒。卒年九十六岁。与刘长卿友善，其赴上都也，长卿有诗送之，亦与王维为友。《全唐诗》收诗十三首。', '?', '?', 10006, '2014-11-22 15:53:56'),
(10121, '綦毋潜', '綦毋潜（生卒年不详），字孝通，虔州（今江西南康）人，唐代著名诗人。约开元十四年（约726年）前后进士及第，授宜寿（今陕西周至）尉，迁右拾遗，终官著作郎，安史之乱后归隐，游江淮一代，后不知所终。綦毋潜才名盛于当时，与许多著名诗人如：李颀、王维、张九龄、储光羲、孟浩然、卢象、高适、韦应物过从甚密，其诗清丽典雅，恬淡适然，后人认为他诗风接近王维。《全唐诗》收录其诗1卷，共26首，内容多为记述与士大夫寻幽访隐的情趣，代表作《春泛若耶溪》选入《唐诗三百首》。', '?', '?', 10006, '2014-11-22 15:59:58'),
(10122, '元结', '元结，字次山，号漫叟、聱叟，唐代文学家、洛阳（今河南洛阳）人，后迁汝州鲁山（今属河南），天宝六载（747）应举落第后，归隐商余山。天宝十二载进士及第。安禄山反，曾率族人避难猗玗洞 （今湖北大冶境内），因号猗玗子。乾元二年（759），任山南东道节度使史翙幕参谋，招募义兵，抗击史思明叛军，保全十五城。代宗时，任道州刺史，调容州，加封容州都督充本管经略守捉使，政绩颇丰。约大历七年（约772年）入朝，后卒于长安。', '719', '772', 10006, '2014-11-22 21:06:47'),
(10123, '陈子昂', '陈子昂，梓州射洪（今四川射洪县）人，字伯玉。唐代诗人，初唐诗文革新人物之一。因曾任右拾遗，后世称陈拾遗。青少年时轻财好施，慷慨任侠。24岁举进士，以上书论政得到武则天重视，授麟台正字。后升右拾遗，直言敢谏。曾因“逆党”反对武后而株连下狱。在26岁、36岁时两次从军边塞，对边防颇有些远见。38岁（圣历元年698）时，因父老解官回乡，不久父死。居丧期间，权臣武三思指使射洪县令段简罗织罪名，加以迫害，冤死狱中。其存诗共100多首，其诗风骨峥嵘，寓意深远，苍劲有力。其中最有代表性的有组诗《感遇》38首，《蓟丘览古》8首和《登幽州台歌》。', '659', '700', 10006, '2014-11-23 10:57:13'),
(10124, '李颀', '李颀，生卒年不详，字、号均不详，汉族，河南颍阳（今河南登封市西）人 ，唐代诗人。开元十三年中进士，做过新乡县尉的小官，诗以写边塞题材为主，风格豪放，慷慨悲凉，七言歌行尤具特色。', '?', '?', 10006, '2014-11-23 10:59:31'),
(10125, '韩愈', '韩愈，字退之，唐代文学家、哲学家、思想家，河阳（今河南省孟州市）人，汉族，郡望河北昌黎，世称韩昌黎。晚年任吏部侍郎，又称韩吏部。是唐代古文运动的倡导者。谥号“文”，又称韩文公。后人尊称他为“唐宋八大家”之首，与柳宗元并称“韩柳”，有“文章巨公”和“百代文宗”之名。曾积极参加讨伐淮西叛藩吴元济的战争，任裴度的行军司马。思想上，韩愈崇奉儒学，力排佛老。著有《韩昌黎集》四十卷，《外集》十卷，《师说》等等。他提出的文道合一、气盛言宜、务去陈言、文从字顺等散文的写作理论，对后人很有指导意义。', '768', '824', 10006, '2014-11-24 09:51:39'),
(10126, '高适', '高适，字达夫、仲武，汉族，唐朝渤海郡（今河北景县）人，后迁居宋州宋城（今河南商丘睢阳）。唐代著名的边塞诗人，曾任刑部侍郎、散骑常侍、渤海县，世称高常侍。高适与岑参并称“高岑”，有《高常侍集》等传世，其诗笔力雄健，气势奔放，洋溢着盛唐时期所特有的奋发进取、蓬勃向上的时代精神。开封禹王台五贤祠即专为高适、李白、杜甫、何景明、李梦阳而立。后人又把高适、岑参、王昌龄、王之涣合称“边塞四诗人”。', '700', '765', 10006, '2015-01-02 11:20:51'),
(10127, '李隆基', '唐玄宗李隆基，公元685年出生在神都洛阳，712年至756年在位。唐朝在位最久的皇帝，唐睿宗第三子，母窦德妃。庙号“玄宗”，又因其谥号为“至道大圣大明孝皇帝”，故亦称为唐明皇。清朝为避讳康熙皇帝之名“玄烨”，多称其为唐明皇，另有尊号“开元圣文神武皇帝”。唐隆元年（710年）六月庚子日申时，李隆基与太平公主联手发动“唐隆政变”诛杀韦后。712年李旦禅位于李隆基，后赐死太平公主，取得了国家的最高统治权。前期注意拨乱反正，任用姚崇、宋璟等贤相，励精图治，他的开元盛世是唐朝的极盛之世，在位后期宠爱杨贵妃，怠慢朝政，宠信奸臣李林甫、杨国忠等，加上政策失误和重用安禄山等塞外民族试图来稳定唐王朝的边疆，结果导致了后来长达八年的安史之乱，为唐朝中衰埋下伏笔。756年李亨即位，尊其为太上皇。762年病逝，葬于泰陵。', '685', '762', 10006, '2014-11-25 09:51:56'),
(10128, '杜审言', '杜审言，字必简，唐朝襄州襄阳（今湖北襄阳）人，后迁居河南巩县，官修文馆直学士。唐高宗咸亨进士，唐中宗时，因与张易之兄弟交往，被流放峰州（今越南越池东南）。曾任隰城尉、洛阳丞等小官，累官修文馆直学士，与李峤、崔融、苏味道被称为“文章四友”，是唐代“近体诗”的奠基人之一，作品多朴素自然。其五言律诗，格律谨严。原有集，已散佚，后人辑有《杜审言诗集》。', '645', '708', 10006, '2014-11-25 09:58:54'),
(10129, '沈佺期', '沈佺期，字云卿，相州内黄（今安阳市内黄县）人，唐代诗人。与宋之问齐名，称“ 沈宋 ”。善属文，尤长七言之作。擢进士第。长安中，累迁通事舍人，预修《三教珠英》，转考功郎给事中。坐交张易之，流驩州。稍迁台州录事参军。神龙中，召见，拜起居郎，修文馆直学士，历中书舍人，太子少詹事。开元初卒。建安后，讫江左，诗律屡变，至沈约、庾信，以音韵相婉附，属对精密，及佺期与宋之问，尤加靡丽。回忌声病，约句准篇，如锦绣成文，学者宗之，号为沈宋。语曰：苏李居前，沈宋比肩。集十卷，今编诗三卷。', '656', '715', 10006, '2014-11-25 10:04:59'),
(10130, '宋之问', '宋之问，字延清，一名少连，汉族，汾州隰城（今山西汾阳市）人，一说虢州弘农（今河南灵宝县）人，初唐时期的诗人，与沈佺期并称“沈宋”。', '656', '712', 10006, '2014-11-25 10:09:55'),
(10131, '韩翃', '韩翃，生卒年不详，字君平，南阳（今河南南阳）人，唐代诗人。是“大历十才子”之一。天宝13年（754）考中进士，宝应年间在淄青节度使侯希逸幕府中任从事，后随侯希逸回朝，闲居长安十年。建中年间，因作一首《寒食》被唐德宗所赏识，因而被提拔为中书舍人。韩翃的诗笔法轻巧，写景别致，在当时传诵很广泛。', '?', '?', 10006, '2014-11-28 12:55:27'),
(10132, '刘慎虚', '刘慎虚，亦字挺卿，号易轩。生卒年不详。新吴（今江西奉新县）人。8岁能属文，上书，召见，拜童子郎。开元二十一年（733）进士，调洛阳尉，迁夏县令。性高逸，不慕荣利，交游多山僧道侣。曾拟在庐山卜宅隐居，未成。早年逝世。', '?', '?', 10006, '2014-11-28 12:58:06'),
(10133, '戴叔伦', '戴叔伦，唐代诗人，字幼公（一作次公），润州金坛（今属江苏）人。年轻时师事萧颖士。曾任新城令、东阳令、抚州刺史、容管经略使。晚年上表自请为道士。其诗多表现隐逸生活和闲适情调，但《女耕田行》、《屯田词》等篇也反映了人民生活的艰苦。论诗主张“诗家之景，如蓝田日暖，良玉生烟，可望而不可置于眉睫之前”。其诗体裁皆有所涉猎。今存诗二卷，多混入宋元明人作品，需要仔细辨伪。', '732', '789', 10006, '2014-11-28 13:00:57'),
(10134, '张籍', '张籍，字文昌，唐代诗人，和州乌江（今安徽和县）人。郡望苏州吴（今江苏苏州），汉族，先世移居和州，遂为和州乌江（今安徽和县乌江镇）人。世称“张水部”、“张司业”。张籍为韩门大弟子，其乐府诗与王建齐名，并称“张王乐府”，著名诗篇有《塞下曲》《征妇怨》《采莲曲》《江南曲》《秋思》等。', '766', '830', 10006, '2014-11-29 23:04:01'),
(10135, '许浑', '许浑，生卒年不详，字用晦（一作仲晦），唐代诗人，润州（今江苏镇江）人。晚唐最具影响力的诗人之一，七五律尤佳，唯诗中多描写水、雨之景，后人拟之与诗圣杜甫齐名，并以“许浑千首湿，杜甫一生愁”评价之。代表作有《咸阳城东楼》。', '?', '?', 10006, '2014-11-29 23:50:49'),
(10136, '马戴', '马戴，字虞臣，定州曲阳（今河北省曲阳县）人。晚唐诗人、官员。早年屡试不第，武宗会昌四年（844年）始中进士，同榜有项斯、赵嘏。大中元年（847年），太原李司空辟掌书记，后以正言被斥为龙阳（今湖南省汉寿县）尉。终太学博士。曾隐居华山，并遨游边关。咸通年间，应辟佐大同军幕府，与贾岛、许棠相唱答。咸通七年（867年），擢拔为国子、太常博士。工于诗，叶矫然称：“晚唐之马戴，盛唐之摩诘也。”杨慎称其《楚江怀古》“猿啼洞庭树，人在木兰舟”一联，谓“虽柳吴兴（柳恽）无以过也”。纪昀认为“晚唐诗人，马戴骨格最高”。咸通九年（869年），病卒于太常博士任上。有《会昌进士诗集》1卷，《补遗》1卷。', '799', '869', 10006, '2014-12-01 00:10:15'),
(10137, '张乔', '张乔，唐代诗人，生卒年不详，今安徽贵池人，懿宗咸通中年进士，当时与许棠、郑谷、张宾等东南才子称“咸通十哲”。黄巢起义时，隐居九华山以终。其诗多写山水自然，不乏清新之作诗清雅巧思，风格也似贾岛。《全唐诗》录存其诗二卷。事见《唐诗纪事》卷十七，《唐才子传》卷十。', '?', '?', 10006, '2014-12-01 00:16:56'),
(10138, '崔涂', '崔涂，字礼山，今浙江富春江一带人，唐末诗人，生卒年、生平均不详。唐僖宗光启四年（888）进士，《全唐诗》存其诗1卷，他写的最有名的一首诗是《除夜有怀》。', '?', '?', 10006, '2014-12-01 00:20:57'),
(10139, '杜荀鹤', '杜荀鹤，字彦之，号九华山人，池州石埭（今石台县贡溪乡杜村）人。晚唐诗人。相传为杜牧已出之妾所生，排行第十五，故称杜十五。幼好学。四十六岁才中进士。五代梁太祖（朱温）时，授员外郎、知制诰；最后任翰林学士，仅五日而卒，恃强胡为。工于诗，有“风暖鸟声碎，日高光鼎重”之句。天佑初卒。著有《唐风集》三卷，顾云为之序，“其壮语大言，则决起逸发，可以左揽工部袂，右拍翰林肩。”。严羽《沧浪诗话·诗体》列有“杜苟鹤体”。', '846', '904', 10006, '2014-12-01 00:33:19'),
(10140, '韦庄', '韦庄，字端己，京兆杜陵（今陕西省西安市）人。晚唐政治家，诗人。广明元年（880年）韦庄在长安应举，黄巢攻占长安以后，与弟妹失散，浪迹天涯。中和三年（883年）三月，在洛阳写有长篇歌行《秦妇吟》。昭宗乾宁元年（894年）进士，曾任校书郎、左补阙等职。乾宁四年（897年），李询为两川宣谕和协使，聘用他为判官。在四川时为王建掌书记，蜀开国制度皆庄所定，官至吏部尚书，同平章事，武成三年（910年）八月，卒于成都花林坊。葬白沙之阳。谥文靖。', '836', '910', 10006, '2015-01-02 11:22:28'),
(10141, '僧皎然', '僧皎然，生卒年不详，湖州人，俗姓谢，字清昼，是中国山水诗创始人谢灵运的后代，是唐代最有名的诗僧、茶僧。南朝谢灵运十世孙。活动于大历、贞元年间，有诗名。他的《诗式》为当时诗格一类作品中较有价值的一部。其诗清丽闲淡，多为赠答送别、山水游赏之作。在《全唐诗》编其诗为815-821共7卷，他为后人留下了470首诗篇，在文学、佛学、茶学等许多方面有深厚造诣， 堪称一代宗师。', '?', '?', 10006, '2014-12-01 00:40:14'),
(10142, '祖咏', '祖咏，生卒年不详，字、号均不详， 唐代诗人，洛阳（今河南洛阳）人。少有文名，擅长诗歌创作。与王维友善。王维在济州赠诗云："结交二十载，不得一日展。贫病子既深，契阔余不浅。"（《赠祖三咏》）其流落不遇的情况可知。开元十二年（724），进士及第，长期未授官。后入仕，又遭迁谪，仕途落拓，后归隐汝水一带。', '?', '?', 10006, '2014-12-01 09:48:07'),
(10143, '崔曙', '崔曙，定州（今河北定州）人。居宋州（今河南登封）。开元二十六年状元及第，官河南尉。晚年隐居河南嵩山。事迹无考。卒于唐玄宗开元二十七年（739年）。《明堂火珠》一诗，有“夜来双月合，曙后一星孤”之句。', '?', '739', 10006, '2014-12-01 09:50:53'),
(10144, '王绩', '王绩，字无功，号东皋子，绛州龙门（今山西河津）人，唐代诗人。隋末举孝廉，除秘书正字。不乐在朝，辞疾，复授扬州六合丞。时天下大乱，弃官还故乡。唐武德中，诏以前朝官待诏门下省。贞观初，以疾罢归河渚间，躬耕东皋，自号“东皋子”。性简傲，嗜酒，能饮五斗，自作《五斗先生传》 ，撰 《酒经》 、《酒谱》 。其诗近而不浅，质而不俗，真率疏放，有旷怀高致，直追魏晋高风。律体滥觞于六朝，而成型于隋唐之际，无功实为先声。', '589', '644', 10006, '2014-12-01 14:20:19'),
(10145, '皇甫冉', '皇甫冉，字茂政，润州丹阳（今江苏镇江）人，唐代诗人。先世居甘肃泾州。天宝十五年进士。曾官无锡尉，大历初入河南节度使王缙幕，终左拾遗、右补阙。其诗清新飘逸，多飘泊之感。', '718', '771', 10006, '2014-12-02 09:02:51'),
(10146, '薛逢', '薛逢，生卒年不详，字陶臣，蒲州河东（今山西永济市）人，唐代诗人。会昌元年（公元八四一）进士。历侍御史、尚书郎。因恃才傲物，议论激切，屡忤权贵，故仕途颇不得意。《全唐诗》收录其诗一卷。《旧唐书》卷一九零，《新唐书》卷二零三皆有传。', '?', '?', 10006, '2014-12-02 13:05:48'),
(10147, '秦韬玉', '秦韬玉，生卒年不详，字仲明，京兆长安（今陕西西安）人，或云郃阳（今陕西合阳）人，唐代诗人。出生于尚武世家，父为左军军将。少有词藻，工歌吟，却累举不第，后谄附当时有权势的宦官田令孜，充当幕僚，官丞郎，判盐铁。黄巢起义军攻占长安后，韬玉从僖宗入蜀，中和二年（882）特赐进士及第，编入春榜。田令孜又擢其为工部侍郎、神策军判官。时人戏为“巧宦”，后不知所终。', '?', '?', 10006, '2014-12-02 13:08:02'),
(10148, '裴迪', '裴迪，生卒年不详，字、号均不详，唐代诗人，关中（今属陕西）人。官蜀州刺史及尚书省郎。其一生以诗文见称，是盛唐著名的山水田园诗人之一。', '?', '?', 10006, '2014-12-02 19:05:13'),
(10149, '李端', '李端，生卒年不详，字正已，赵州（今河北赵县）人，唐代诗人。少居庐山，师诗僧皎然。大历五年进士。曾任秘书省校书郎、杭州司马。晚年辞官隐居湖南衡山，自号衡岳幽人。今存《李端诗集》三卷。其诗多为应酬之作，多表现消极避世思想，个别作品对社会现实亦有所反映，一些写闺情的诗也清婉可诵，其风格与司空曙相似。李端是大历十才子之一。今存《李端诗集》三卷。', '?', '?', 10006, '2014-12-03 09:11:31'),
(10150, '王建', '王建（生卒年不详），字仲初，颍川（今河南许昌）人，唐朝诗人。出身寒微，一生潦倒。曾一度从军，约46岁始入仕，曾任昭应县丞、太常寺丞等职。后出为陕州司马，世称王司马。约64岁为光州刺史。与张籍友善，乐府与张齐名，世称张王乐府。其诗题材广泛，生活气息浓厚，思想深刻。善于选择有典型意义的人、事和环境加以艺术概括，集中而形象地反映现实，揭露矛盾。多用比兴、白描、对比等手法，常在结尾以重笔突出主题。体裁多为七言歌行，篇幅短小。语言通俗凝练，富有民歌谣谚色彩。', '?', '?', 10006, '2014-12-03 09:18:05'),
(10151, '权德舆', '权德舆， 唐代文学家，大臣。字载之，行三。天水略阳（今甘肃秦安）人，家于润州丹阳（今江苏丹阳）。名士权皋子。未冠，即以文章称，杜佑、裴胄交辟之。德宗闻其材，召为太常博士，改左补阙，兼制诰，进中书舍人，历礼部侍郎，三知贡举。宪宗元和初，历兵部、吏部侍郎，坐郎吏误用官阙，改太子宾客。俄复前官，迁太常卿，拜礼部尚书，同平章事。会李吉甫再秉政，帝又自用李绛，议论持异，德舆从容不敢有所轻重，坐是罢，以检校吏部尚书留守东都。复拜太常卿，徙刑部尚书，出为山南西道节度使。二年，以病乞还，卒于道，年六十。赠左仆射，谥曰文。', '759', '818', 10006, '2014-12-03 09:20:58'),
(10152, '张祜', '张祜（生卒年不详），字承吉，邢台清河（一说山东德州）人，唐代诗人。出生在清河张氏望族，家世显赫，被人称作张公子，有“海内名士”之誉。张祜的一生，在诗歌创作上取得了卓越成就。“故国三千里，深宫二十年”张祜以是得名，《全唐诗》收录其349首诗歌。', '?', '?', 10006, '2014-12-03 12:41:46'),
(10153, '贾岛', '贾岛，字浪仙，又名瘦岛，唐代诗人。汉族，唐朝河北道幽州范阳县（今河北省涿州市）人。早年出家为僧，号无本。自号“碣石山人”。据说在长安（今陕西西安）的时候因当时有命令禁止和尚午后外出，贾岛做诗发牢骚，被韩愈发现其才华，并成为“苦吟诗人”。后受教于韩愈，并还俗参加科举，但累举不中第。唐文宗的时候被排挤，贬做长江（今四川蓬溪县）主簿。唐武宗会昌年初由普州司仓参军改任司户，未任病逝。', '779', '843', 10006, '2014-12-03 12:44:22'),
(10154, '李频', '李频（生卒年不详），字德新，唐大中元年（847），唐寿昌长林西山人，唐代后期诗人。幼读诗书，博览强记，领悟颇多。寿昌县令 穆君 游灵栖洞，即景吟诗：“一径入双崖，初疑有几家。行穷人不见，坐久日空斜”。得此四句后稍顿未续。时李频从行，续吟：“石上生灵笋，池中落异花。终须结茅屋，到此学餐霞。”穆君大为赞赏。但此诗根据史学家考证是李频本人所作。', '?', '?', 10006, '2014-12-03 12:46:42'),
(10155, '金昌绪', '金昌绪（生卒年不详），生平不详，唐临安（今浙江省杭州市）人。今存诗《春怨》一首。', '?', '?', 10006, '2014-12-03 12:49:07'),
(10156, '无名氏', '唐代佚名作者的统称。', '?', '?', 10006, '2014-12-03 12:50:59'),
(10157, '贺知章', '贺知章，字季真，晚年自号四明狂客，汉族，唐代著名诗人、书法家，越州永兴（今浙江萧山）人。少时就以诗文知名。唐武后（武则天）证圣元年（695）中乙未科状元，授予国子四门博士，迁太常博士。后历任礼部侍郎、秘书监、太子宾客等职。为人旷达不羁，有“清谈风流”之誉，晚年尤纵，自号“四明狂客”、“秘书外监”。八十六岁告老还乡，旋逝。属于盛唐前期诗人，又是著名书法家。作品大多散佚，现仅存二十首。', '659', '744', 10006, '2014-12-03 22:09:36'),
(10158, '张旭', '张旭（生卒年不详），字伯高，一字季明，汉族，唐朝吴县（今江苏苏州）人，开元、天宝时在世，曾任常熟县尉，金吾长史。以草书著名，与李白诗歌，裴旻剑舞，称为。诗亦别具一格，以七绝见长，与李白、贺知章等人共列饮中八仙之一。与贺知章、张若虚、包融号称“吴中四士”。书法与怀素齐名。性好酒，每醉后号呼狂走，索笔挥洒，时称张颠。被后世尊称为“草圣”。', '?', '?', 10006, '2014-12-03 22:15:09'),
(10159, '刘方平', '刘方平（生卒年不详），字、号均不详，唐玄宗天宝年间诗人，洛阳（今河南洛阳）人，生平事迹不详。约公元七五八年前后在世，匈奴族。天宝前期曾应进士试，又欲从军，均未如意，从此隐居颍水、汝河之滨，终生未仕。与皇甫冉、元德秀、李颀、严武为诗友，为薪颖士赏识。工诗，善画山水。其诗多咏物写景之作，尤擅绝句，其诗多写闺情、乡思，思想内容较贫弱，但艺术性较高，善于寓情于景，意蕴无穷。其《月夜》 、《春怨》 、《新春》、《秋夜泛舟》等都是历来为人传诵的名作。', '?', '?', 10006, '2014-12-04 00:24:50'),
(10160, '柳中庸', '柳中庸（生卒年不详），名淡，中庸是其字，唐代边塞诗人。河东（今山西永济）人，为柳宗元族人。大历年间进士，曾官鸿府户曹，未就。萧颖士以女妻之。与弟中行并有文名。与卢纶、李端为诗友。所选《征人怨》是其流传最广的一首。《全唐诗》存诗仅13首。其诗以写边塞征怨为主，然意气消沉，无复盛唐气象。', '?', '?', 10006, '2014-12-04 00:29:01'),
(10161, '顾况', '顾况，生卒年不详，字逋翁，号华阳真逸（一说华阳真隐），晚年自号悲翁，汉族，苏州海盐横山人（今在浙江海宁境内），唐代诗人、画家、鉴赏家。他一生官位不高，曾任著作郎，因作诗嘲讽得罪权贵，贬饶州司户参军。晚年隐居茅山。', '?', '?', 10006, '2014-12-04 09:13:16'),
(10162, '朱庆馀', '朱庆馀（生卒年不详），名可久，字庆馀，以字行，越州（今浙江绍兴）人，唐代诗人。宝历二年（826）进士，官至秘书省校书郎，见《唐诗纪事》卷四六、《唐才子传》卷六，《全唐诗》存其诗两卷。曾作《闺意献张水部》作为参加进士考试的“通榜”，增加中进士的机会。据说张籍读后大为赞赏，写诗回答他说：“越女新装出镜心，自知明艳更沉吟。 齐纨未足时人贵，一曲菱歌值万金。”于是朱庆馀声名大震。', '?', '?', 10006, '2014-12-04 09:24:46'),
(10163, '郑畋', '郑畋，字台文。荥阳（今属河南）人，唐末宰相。以镇压黄巢民变而知名。', '825', '883', 10006, '2014-12-04 14:06:10'),
(10164, '韩偓', '韩偓，晚唐五代诗人，字致光，号致尧，晚年又号玉山樵人。陕西万年县（今樊川）人。自幼聪明好学，10岁时，曾即席赋诗送其姨夫李商隐，令满座皆惊，李商隐称赞其诗是“雏凤清于老凤声”。龙纪元年（889年），韩偓中进士，初在河中镇节度使幕府任职，后入朝历任左拾遗、左谏议大夫、度支副使、翰林学士。', '842', '923', 10006, '2014-12-04 14:08:43'),
(10165, '张泌', '张泌，生卒年不详，《全唐诗》作曰字子澄，安徽淮南人。五代后蜀词人。是花间派的代表人物之一。其词用字工炼，章法巧妙，描绘细腻，用语流便。', '?', '?', 10006, '2014-12-04 14:11:45'),
(10166, '杜秋娘', '杜秋，生卒年不详，资治通鉴称杜仲阳，后世多称为“杜秋娘”，是唐代金陵人。15岁时成了李锜的妾侍。元和二年（807年），李锜正式起兵造反。后来李锜造反失败，杜秋被纳入宫中。后受到唐宪宗宠幸。元和十五年（820年）唐穆宗即位，任命她为儿子李凑的傅姆。后来李凑被废去漳王之位，杜秋赐归故乡。', '?', '?', 10006, '2014-12-04 14:34:54'),
(10167, '赵佶', '宋徽宗赵佶，宋神宗第十一子、宋哲宗之弟，宋朝第八位皇帝。先后被封为遂宁王、端王。哲宗于公元1100年正月病逝时无子，向皇后于同月立他为帝。第二年改年号为“建中靖国”。在位26年（1100年2月23日~1126年1月18日），国亡被俘受折磨而死，终年54岁，葬于都城绍兴永佑陵（今浙江省绍兴市柯桥区东南35里处）。他自创一种书法字体被后人称之为“瘦金体”。是古代少有的艺术天才与全才。被后世评为“宋徽宗诸事皆能，独不能为君耳！”', '1082', '1135', 10001, '2014-12-16 22:56:44'),
(10168, '钱惟演', '钱惟演，北宋大臣，西昆体骨干诗人。字希圣，钱塘（今浙江杭州）人。吴越忠懿王钱俶第十四子，刘娥之兄刘美的妻舅。从俶归宋，历右神武将军、太仆少卿、命直秘阁，预修《册府元龟》，累迁工部尚书，拜枢密使，官终崇信军节度使，博学能文，所著今存《家王故事》、《金坡遗事》。有三子钱暧、钱晦（宋太宗第九女献穆大长公主之女）、钱暄。孙子为康国公钱景臻（娶宋仁宗大长公主）。另外，盛度为其女婿。', '962', '1034', 10001, '2014-12-16 23:02:23'),
(10169, '张先', '张先，字子野，乌程（今浙江湖州吴兴）人。北宋时期著名的词人，曾任安陆县的知县，因此人称“张安陆”。天圣八年进士，官至尚书都官郎中。晚年退居湖杭之间，曾与梅尧臣、欧阳修、苏轼等游。善作慢词，与柳永齐名，造语工巧，曾因三处善用“影”字，世称张三影。', '990', '1078', 10001, '2014-12-16 23:11:30'),
(10170, '晏殊', '晏殊，字同叔，抚州临川（今南昌进贤）人。北宋著名文学家、政治家。生于宋太宗淳化二年（991），十四岁以神童入试，赐进士出身，命为秘书省正字，官至右谏议大夫、集贤殿学士、同平章事兼枢密使、礼部刑部尚书、观文殿大学士知永兴军、兵部尚书，封临淄公，谥号元献，世称晏元献。晏殊以词著于文坛，尤擅小令，风格含蓄婉丽，与其子晏几道，被称为“大晏”和“小晏”，又与欧阳修并称“晏欧”；亦工诗善文，原有集，已散佚。存世有《珠玉词》、《晏元献遗文》、《类要》残本。', '991', '1055', 10001, '2014-12-17 09:59:30'),
(10171, '王安国', '王安国，字平甫，王安石大弟。熙宁进士。北宋临川（今江西省抚州市临川区邓家巷）人。北宋著名诗人。世称王安礼、王安国、王雱为“临川三王”。王安国器识磊落，文思敏捷，曾巩谓其“于书无所不通，其明于是非得失之理为尤详，其文闳富典重，其诗博而深。', '1028', '1074', 10001, '2014-12-17 15:03:40'),
(10172, '王观', '王观，字通叟，如皋（现属江苏南通）人，宋代词人。王安石为开封府试官时，科举及第。宋仁宗嘉佑二年（1057）考中进士。后历任大理寺丞、江都知县等，相传曾奉诏作《清平乐》一首，描写宫廷生活。高太后对王安石等变法不满，认为王观属于王安石门生，就以《清平乐》亵渎了宋神宗为名，第二天便将王观罢职。王观于是自号“逐客”，从此以一介平民生活。王观代表作有《卜算子-送鲍浩然之浙东》、《临江仙-离杯》、《高阳台》等，其中《卜算子》一词以水喻眼波，以山指眉峰；设喻巧妙，又语带双关，写得妙趣横生，堪称杰作。《红芍药》词写人生短暂，从而提出人生应追欢及早，写法亦颇有特色。', '1035', '1100', 10001, '2014-12-17 18:23:09'),
(10173, '韩缜', '韩缜，字玉汝，原籍灵寿（今属河北）人，徙雍丘（今河南杞县）。韩绛、韩维之弟。庆历二年进士。英宗时任淮南转运使，神宗时自龙图阁直学士进知枢密院事。曾出使西夏。哲宗立，拜尚书右仆射兼中书侍郎，罢知颍昌府。绍圣四年卒，年七十九，谥庄敏，封崇国公。《宋史》、《东都事略》有传。《全宋词》录其词一首。', '1019', '1097', 10001, '2014-12-18 10:56:36'),
(10174, '宋祁', '宋祁，字子京，宋庠之弟，北宋官员、文学家、史学家。祖籍安陆（今湖北安陆县），曾祖宋骈徙居开封府雍丘（今河南民权县双塔集村）。天圣二年进士，官翰林学士、史馆修撰。与欧阳修等合修《新唐书》，书成，进工部尚书，拜翰林学士承旨。卒谥景文，与兄宋庠并有文名，时称“二宋”。诗词语言工丽，因《玉楼春》词中有“红杏枝头春意闹”句，世称“红杏尚书”。', '998', '1061', 10001, '2014-12-18 11:03:23'),
(10175, '毛滂', '毛滂，字泽民，号东堂，衢州江山石门（今属浙江江山市）人，北宋知名词人。著有《东堂集》、《东堂词》传世。其词受苏轼、柳永影响，清圆明润，别树一格，无秾艳词语，自然深挚、秀雅飘逸，对陈与义、朱敦儒、姜白石、张炎等人皆有影响。', '1064', '?', 10001, '2014-12-18 15:16:22'),
(10176, '陈克', '陈克，字子高，自号赤城居士。南宋临海（今属浙江）人。少随父陈贻序宦学四方，早年为敕令所删定官，后辟为右承事郎都督府准备差遣。宋高宗绍兴五年（1135年），兵部尚书吕祉抗金，陈克随军队北上。绍兴七年（1137年），随吕祉去庐州收编王德、郦琼的部队。吕祉被杀，叛军要陈克屈膝，陈克厉声答曰：“吾为宋臣，学忠信之道，宁为玉碎，不为瓦全。”，不幸被“积薪焚死”，临死时仍“骂不绝口，声如雷震”，时人称“国士”。', '1081', '1137', 10001, '2014-12-18 15:20:32'),
(10177, '李元膺', '李元膺，东平（今属山东）人，宋代词人。生平未详。绍圣间，李孝美作《墨谱法式》，元膺为序。又蔡京在翰苑，因赐宴西池，失足落水，几至沉溺，元膺闻之笑曰：“蔡元长都湿了肚里文章。”京闻之怒，卒不得召用。据此，元膺当为哲宗、徽宗时人。近人赵万里辑有《李元膺词》一卷，凡九首。', '?', '?', 10001, '2014-12-18 15:24:54'),
(10178, '时彦', '时彦，字邦彦，河南开封（今河南开封）人。宋神宗元丰二年（1079）己未科状元。存词仅一首。', '?', '1107', 10001, '2014-12-18 15:27:53'),
(10179, '李之仪', '李之仪，北宋词人，字端叔，自号姑溪居士、姑溪老农。汉族，沧州无棣（今山东省庆云县）人。哲宗元祐初为枢密院编修官，通判原州。元祐末从苏轼于定州幕府，朝夕倡酬。元符中监内香药库，御史石豫参劾他曾为苏轼幕僚，不可以任京官，被停职。徽宗崇宁初提举河东常平。后因得罪权贵蔡京，除名编管太平州（今安徽当涂），后遇赦复官，晚年卜居当涂。著有《姑溪词》一卷、《姑溪居士前集》五十卷和《姑溪题跋》二卷。', '1048', '1117', 10001, '2014-12-18 15:30:22'),
(10180, '晁端礼', '晁端礼，北宋词人。名一作元礼。字次膺。开德府清丰县（今属河南）人，因其父葬于济州任城（今山东济宁），遂为任城人。一说徙家彭门（今江苏徐州）。晁补之称他为十二叔，常与唱和。神宗熙宁六年（1073）举进士，历单州城武主簿、瀛州防御推官，知洺州平恩县（今河北邱县），官满授泰宁军节度推官，迁知大名府莘县事。因得罪上司，废徙达30年之久。', '1046', '1113', 10001, '2014-12-18 20:44:22'),
(10181, '赵令畤', '赵令畤，初字景贶，苏轼为之改字德麟，自号聊复翁。太祖次子燕王德昭﹝赵德昭﹞玄孙。元祐中签书颍州公事，时苏轼为知州，荐其才于朝。后坐元祐党籍，被废十年。绍兴初，袭封安定郡王，迁宁远军承宣使。四年卒，赠开府仪同三司。著有《侯鲭录》八卷，赵万里为辑《聊复集》词一卷。', '1061', '1134', 10001, '2014-12-18 20:46:51'),
(10182, '晁补之', '晁补之，字无咎，号归来子，汉族，济州巨野（今属山东巨野县）人，北宋时期著名文学家。为“苏门四学士”（另有北宋诗人黄庭坚、秦观、张耒）之一。曾任吏部员外郎、礼部郎中。 工书画，能诗词，善属文。与张耒并称“晁张”。其散文语言凝练、流畅，风格近柳宗元。诗学陶渊明。其词格调豪爽，语言清秀晓畅，近苏轼。但其诗词流露出浓厚的消极归隐思想。著有《鸡肋集》、《晁氏琴趣外篇》等。', '1053', '1110', 10001, '2014-12-18 20:53:04'),
(10183, '朱服', '朱服，字行中，湖州乌程（今浙江吴兴）人。熙宁六年（1073）进士。累官国子司业、起居舍人，以直龙图阁知润州，徙泉州、婺州等地。哲宗朝，历官中书舍人、礼部侍郎。徽宗时，任集贤殿修撰，后知广州，黜知泉州，再贬蕲州安置，改兴国军卒。《全宋词》存其词一首，格调凄苍。', '1048', '?', 10001, '2014-12-19 10:34:21'),
(10184, '张元干', '张元干，字仲宗，号芦川居士、真隐山人，晚年自称芦川老隐。芦川永福人（今福建永泰嵩口镇月洲村人）。历任太学上舍生、陈留县丞。金兵围汴，秦桧当国时，入李纲麾下，坚决抗金，力谏死守。曾赋《贺新郎》词赠李纲，后秦桧闻此事，以他事追赴大理寺除名削籍。元干尔后漫游江浙等地，客死他乡，卒年约七十，归葬闽之螺山。张元干与张孝祥一起号称南宋初期“词坛双璧”。', '1091', '1161', 10001, '2014-12-20 10:40:15'),
(10185, '汪藻', '汪藻，北宋末、南宋初文学家。字彦章，号浮溪，又号龙溪，饶州德兴（今属江西）人。汪谷之子。先世籍贯婺源，后移居饶州德兴（今属江西）。其著名代表作是《建炎三年十一月三日德音》。', '1079', '1154', 10001, '2014-12-20 10:48:58'),
(10186, '李玉', '李玉，宋朝词人，生平不详。《全宋词》收录其词一首。', '?', '?', 10001, '2014-12-20 10:52:33'),
(10187, '廖世美', '廖世美是生活于南北宋之交的一位词人，生平无考，据传是安徽省东至县廖村人。现存词两首（一说三首），均见于《唐宋诸贤绝妙词选》。', '?', '?', 10001, '2014-12-20 10:53:53'),
(10188, '吕滨老', '吕滨老，一作吕渭老，字圣求，生卒年不详。嘉兴（今属浙江）人。北宋宣和末年朝士。有《圣求词》一卷。存词130余首。', '?', '?', 10001, '2014-12-20 10:55:53'),
(10189, '鲁逸仲', '鲁逸仲，孔夷的隐名，字方平，汝州龙兴（今属河南宝丰）人。孔旼之子。哲宗元祐间隐士，隐居滍阳（今河南），与李廌为诗酒侣，自号滍皋渔父。与李荐、刘攽、韩维为友。王灼《碧鸡漫志》卷二称其与侄孔处度齐名。黄升赞其“词意婉丽，似万俟雅言”（《花庵词选》）。《全宋词》录其词三首。事见《咸淳临安志》卷六六。', '?', '?', 10001, '2014-12-20 10:57:53'),
(10190, '张抡', '张抡，字才甫，自号莲社居士，开封（今属河南）人。里居及生卒年均不详，约宋高宗绍兴末前后在世。好填词，每应制进一词，宫中即付之丝竹。尝于乾道三年，（公元一一六七年）高宗莅聚景园，抡进柳梢青词；淳熙六年（公元一一七九）三月，高宗再莅聚景园，抡进壶中天慢词；九月，孝宗幸绛华宫，抡进临江仙词：均赐赉极渥。抡所著有《莲社词》一卷，存词100余首。《文献通考》及绍兴内府古器评二卷，《四库总目》并传于世。', '?', '?', 10001, '2014-12-20 13:09:13'),
(10191, '程垓', '程垓，字正伯，眉山（今属四川）人。苏轼中表程之才（字正辅）之孙。淳熙十三年（1186）游临安，陆游为其所藏山谷帖作跋，未几归蜀。撰有帝王君臣论及时务利害策五十篇。绍熙三年（1192），已五十许，杨万里荐以应贤良方正科。绍熙五年（1194）乡人王称序其词，谓“程正伯以诗词名，乡之人所知也。余顷岁游都下，数见朝士，往往亦称道正伯佳句”。冯煦《蒿庵论词》：“程正伯凄婉绵丽，与草窗所录《绝妙好词》家法相近。”有《书舟词》（一作《书舟雅词》）一卷。', '?', '?', 10001, '2014-12-20 13:10:56'),
(10192, '韩元吉', '韩元吉，南宋词人。字无咎，号南涧。汉族，开封雍邱（今河南开封市）人，一作许昌（今属河南）人。韩元吉词多抒发山林情趣，如《柳梢青》“云淡秋云”、《贺新郎》“病起情怀恶”等。著有《涧泉集》、《涧泉日记》、《南涧甲乙稿》、《南涧诗余》。存词80余首。', '1118', '1187', 10001, '2014-12-20 13:15:24'),
(10193, '袁去华', '袁去华，字宣卿，江西奉新（一作豫章）人。生卒年均不详，约宋高宗绍兴末前后在世。绍兴十五年（公元1145年）进士。改官知石首县而卒。善为歌词，尝为张孝祥所称。去华著有适斋类稿八卷，词一卷，著有《适斋类稿》、《袁宣卿词》、《文献通考》传于世。存词90余首。', '?', '?', 10001, '2014-12-20 13:17:46'),
(10194, '陆淞', '陆淞，字子逸，号云溪，山阴人，陆佃之孙，陆游胞兄。生卒年均不详，约宋高宗绍兴中前后在世。', '?', '?', 10001, '2014-12-20 13:21:23'),
(10195, '刘一止', '刘一止，字行简，号太简居士，湖州归安（今浙江湖州）人。宣和三年进士，累官中书舍人、给事中，以敷文阁直学士致仕。为文敏捷，博学多才，其诗为吕本中、陈与义所叹赏。有《苕溪集》。', '1078', '1160', 10001, '2014-12-20 13:23:18'),
(10196, '韩疁', '韩疁，宋代词人，字子耕，号萧闲，生卒年不详，有萧闲词一卷，不传。共存词6首。赵万里有辑本。', '?', '?', 10001, '2014-12-20 13:28:16');
INSERT INTO `author` (`id`, `name`, `intro`, `birth_year`, `death_year`, `dynasty_id`, `updated_at`) VALUES
(10197, '李邴', '李邴， 字汉老，号龙龛居士。济州任城人。生于宋神宗元丰八年，卒于高宗绍兴十六年，年六十二岁。崇宁五年，1106年举进士第。累迁翰林学士。高宗即位，擢兵部侍郎，兼直学士院。苗傅、刘正彦反，邴谕以逆顺祸福之理，且密劝殿帅王元，俾以禁旅击贼。后为资政殿学士，上战阵、守备、措画、绥怀各五事，不报。闲居十七年，卒於泉州。谥文敏。邴著有草堂集一百卷，《宋史本传》传于世。存词8首。', '1085', '1146', 10001, '2014-12-20 15:37:58'),
(10198, '蔡伸', '蔡伸，字伸道，号友古居士，莆田（今属福建）人，蔡襄孙。政和五年（1115）进士。宣和年间，出知潍州北海县、通判徐州。赵构以康王开大元帅幕府，伸间道谒军门，留置幕府。南渡后，通判真州，除知滁州。秦桧当国，以赵鼎党被罢，主管台州崇道观。绍兴九年（1139），起知徐州，改知德安府。后为浙东安抚司参谋官，提举崇道观。绍兴二十六年卒，年六十九。《宋史翼》有传。伸少有文名，擅书法，得祖襄笔意。工词，与向子諲同官彭城漕属，屡有酬赠。有《友古居士词》一卷。 存词175首。', '1088', '1156', 10001, '2014-12-20 15:45:12'),
(10199, '周紫芝', '周紫芝，南宋文学家。字少隐，号竹坡居士，宣城（今安徽宣城市）人。绍兴进士。高宗绍兴十五年，为礼、兵部架阁文字。高宗绍兴十七年（1147）为右迪功郎敕令所删定官。历任枢密院编修官、右司员外郎。绍兴二十一年（1151）出知兴国军（治今湖北阳新），后退隐庐山。交游的人物主要有李之仪、吕好问吕本中父子、葛立方以及秦桧等，曾向秦桧父子献谀诗。约卒于绍兴末年。著有《太仓稊米集》、《竹坡诗话》、《竹坡词》。有子周畴。', '1082', '1155', 10001, '2014-12-20 15:46:53'),
(10200, '李甲', '李甲，字景元，居华亭乡，自号华亭逸人。《宋诗纪事补遗》卷三十一年云：“李景元，元符中，嘉善县令。”善填词，工小令，有闻于时。画翎毛有意外趣，但木柯未佳。米芾尝称之。有周泳先本《李景元词》，存词9首；', '?', '?', 10001, '2014-12-20 15:51:36'),
(10201, '李重元', '李重元，生卒年不详，约1122（宋徽宗宣和）前后在世。南宋黄升编《花庵词选》，及《全宋词》收其《忆王孙》词4首，分咏春、夏、秋、冬四季。《婉约词》中收2首 。', '?', '?', 10001, '2014-12-20 15:52:58'),
(10202, '万俟咏', '万俟咏（“万俟”是复姓，读作mò qí），北宋末南宋初词人。字雅言，自号词隐、大梁词隐。籍贯与生卒年均不详。哲宗元佑时已以诗赋见称于时。据王灼《碧鸡漫志》卷2记载：“元佑时诗赋老手”。但屡试不第，于是绝意仕进，纵情歌酒。自号“大梁词隐”。徽宗政和初年，召试补官，授大晟府制撰。绍兴五年（1135）补任下州文学。善工音律，能自度新声。词学柳永，存词27首。', '?', '?', 10001, '2014-12-20 15:54:58'),
(10203, '徐伸', '徐伸，字干臣，三衢（今浙江衢州）人。生卒年均不详，约宋徽宗政和初前后在世。政和初，以知音律为太常典乐。出知常州。善词，', '?', '?', 10001, '2014-12-20 15:56:27'),
(10204, '田为', '田为，生卒年不详，字不伐，籍里无考。善琵琶，通音律。政和末，充大晟府典乐。宣和元年（1119）罢典乐，为乐令。 《全宋词》存词六首，有《芊呕集》 田为才思与万俟咏抗行，词善写人意中事，杂以俗言俚语，曲尽要妙。 尝出含三个词牌的联语“玉蝴蝶恋花心动”，天下无能对者。', '?', '?', 10001, '2014-12-20 18:38:05'),
(10205, '曹组', '曹组，北宋词人。生卒年不详。字元宠。颍昌（今河南许昌）人。一说阳翟（今河南禹县）人。曾官睿思殿应制，因占对才敏，深得宋徽宗宠幸，奉诏作《艮岳百咏》诗。约于徽宗末年去世。存词36首。曹组的词以"侧艳"和"滑稽下俚"著称，在北宋末曾传唱一时，浅薄无聊者纷纷仿效。但在南宋初却受到有识者的批评，甚至鄙弃。一些词描写其羁旅生活，感受真切，境界颇为深远，无论手法、情韵，都与柳永词有继承关系。', '?', '?', 10001, '2014-12-20 18:39:33'),
(10206, '陈亮', '陈亮，原名汝能，后改名陈亮，字同甫，号龙川，婺州永康（今属浙江）人。婺州以解头荐，因上《中兴五论》，奏入不报。孝宗淳熙五年，诣阙上书论国事。后曾两次被诬入狱。绍熙四年光宗策进士第一，状元。授签书建康府判官公事，未行而卒，谥号文毅。所作政论气势纵横，词作豪放，有《龙川文集》《龙川词》，宋史有传。', '1143', '1194', 10001, '2014-12-20 18:43:52'),
(10207, '范成大', '范成大，字致能，号石湖居士。汉族，平江吴县（今江苏苏州）人。南宋诗人，谥文穆。从江西派入手，后学习中、晚唐诗，继承了白居易、王建、张籍等诗人新乐府的现实主义精神，终于自成一家。风格平易浅显、清新妩媚。诗题材广泛，以反映农村社会生活内容的作品成就最高。他与杨万里、陆游、尤袤合称南宋“中兴四大诗人”。', '1126', '1193', 10001, '2014-12-20 18:45:34'),
(10208, '吴文英', '吴文英，字君特，号梦窗，晚年又号觉翁，四明（今浙江宁波）人。原出翁姓，后出嗣吴氏。《宋史》无传。一生未第，游幕终身。于苏、杭、越三地居留最久。并以苏州为中心，北上到过淮安、镇江，苏杭道中又历经吴江垂虹亭、无锡惠山，及茹霅二溪。游踪所至，每有题咏。晚年一度客居越州，先后为浙东安抚使吴潜及嗣荣王赵与芮门下客，后“困踬以死”。有《梦窗词集》一部，存词三百四十余首，分四卷本与一卷本。其词作数量丰沃，风格雅致，多酬答、伤时与忆悼之作，号“词中李商隐”。', '1200', '1260', 10001, '2014-12-21 15:53:24'),
(10209, '章良能', '章良能，字达之，宋代丽水（今属浙江）人，居吴兴。淳熙五年进士，除著作佐郎，宁宗朝官至参知政事。间作小词，极有思致。', '?', '1214', 10001, '2014-12-21 15:57:44'),
(10210, '严仁', '严仁，字次山，号樵溪，邵武（今属福建）人。生卒年均不详，约宋宁宗庆元末前后在世。好古博雅。杨巨源诛吴曦，安丙惎而杀之，仁为作长愤歌，为时传诵。与同族严羽、严参齐名，人称“三严”。仁工词，有《清江欸乃集》不传，《文献通考》行于世。存词30首。', '?', '?', 10001, '2014-12-21 16:05:44'),
(10211, '俞国宝', '俞国宝，字不详，号醒庵。江西抚州临川人。南宋著名诗人。江西诗派著名诗人之一。国宝性豪放，嗜诗酒，曾游览全国名山大川，饮酒赋诗，留下不少脍炙人口的锦词佳篇。著有《醒庵遗珠集》10卷。约宋宁宗庆元初前后在世。孝宗淳熙间为太学生。', '?', '?', 10001, '2014-12-21 16:07:14'),
(10212, '张镃', '张镃，原字时可，因慕郭功甫，故易字功甫，号约斋。先世成纪（今甘肃天水）人，寓居临安（现浙江杭州），卜居南湖。出生显赫，张镃为宋南渡名将张俊曾孙，刘光世外孙。张镃又是宋末著名诗词家张炎的曾祖，是张氏家族由武功转向文阶过程中的重要环节。隆兴二年（1164），为大理司直。淳熙年间直秘阁通判婺州。庆元初为司农寺主簿，迁司农寺丞。开禧三年（1207）与谋诛韩侂胄，又欲去宰相史弥远，事泄，于嘉定四年十二月被除名象州编管，卒于是年后。', '1153', '1221', 10001, '2014-12-21 16:08:37'),
(10213, '史达祖', '史达祖，字邦卿，号梅溪，汴（河南开封）人。一生未中第，早年任过幕僚。韩侂胄当国时，他是最亲信的堂吏，负责撰拟文书。韩败，史牵连受黥刑，死于贫困中。史达祖的词以咏物为长，其中不乏身世之感。他还在宁宗朝北行使金，这一部分的北行词，充满了沉痛的家国之感。今传有《梅溪词》。存词112首。', '1163', '1220', 10001, '2014-12-21 16:11:35'),
(10214, '刘克庄', '刘克庄，初名灼，号后村，莆田人，初为靖安主薄，后长期游幕于江、浙、闽、广等地。诗属江湖派，作品数量丰富，内容开阔，多言谈时政，反应民生之作，早年学晚唐体，晚年诗风趋向江西派。词深受辛弃疾影响，多豪放之作，散文化、议论化倾向也较突出。有《后村先生大全集》。程章灿《刘克庄年谱》对其行迹有较详细考证，侯体健《刘克庄的文学世界》展现了其文学创作各个方面，探索精微。', '1187', '1269', 10001, '2014-12-21 21:38:21'),
(10215, '卢祖皋', '卢祖皋，字申之，一字次夔，号蒲江，永嘉人（今浙江温州人）南宋宁宗庆元五年（1199）中进士，初任淮南西路池州教授、历任秘书省正字、校书郎、著书郎、累官至权直学士院。今诗集不传，遗著有《蒲江词稿》一卷，刊入“彊村丛书”，凡96阕。诗作大多遗失，唯《宋诗记事》、《东瓯诗集》尚存近体诗8首。', '1174', '1224', 10001, '2014-12-21 21:50:04'),
(10216, '潘牥', '潘牥（fāng），字庭坚，号紫岩，初名公筠，避理宗讳改，福州富沙（今属福建）人。端平二年（1235）进士第三名，调镇南军节度推官、衢州推官，皆未上。历浙西茶盐司干官，改宣教郎，除太学正，旬日出通判潭州。淳祐六年卒于官，年四十三。有《紫岩集》，已佚。刘克庄为撰墓志铭。《宋史》、《南宋书》有传。赵万里《校辑宋金元人词》辑有《紫岩词》一卷。存词5首。', '1204', '1246', 10001, '2014-12-21 21:52:33'),
(10217, '陆睿', '陆睿， 字景思，号西云。会稽（今浙江绍兴）人，宋代官吏、词人。绍定5年（1232）进士，官至集英殿修撰，江南东路计度转运副使兼淮西总领。存词三首。代表词作《瑞鹤仙》。', '?', '1266', 10001, '2014-12-21 21:53:55'),
(10218, '黄孝迈', '黄孝迈（生卒年不详），南宋诗人，字德夫，号雪舟。有人说他“妙才超轶，词采溢出，天设神授，朋侪推独步，耆宿避三舍。酒酣耳热，倚声而作者，殆欲摩刘改之（过）、孙季蕃（惟信）之垒”，“其清丽，叔原（晏几道）、方回（贺铸）不能加其绵密。”有《雪舟长短句》。存词4首。', '?', '?', 10001, '2014-12-22 20:11:49'),
(10219, '潘希白', '潘希白，字怀古，号渔庄，永嘉人（今浙江湖州人）。南宋理宗宝祐元年（1253）年中进士。存词1首。', '?', '?', 10001, '2014-12-22 20:13:19'),
(10220, '朱嗣发', '朱嗣发，字士荣，号雪崖，其祖先当建炎、绍兴之际，避兵乌程常乐乡（今浙江湖州）。宋亡前，专志奉亲，宋亡后，举充提学学官，不受。隐士。《阳春白雪》卷八录其词一首。', '?', '?', 10001, '2014-12-22 20:15:25'),
(10221, '刘辰翁', '刘辰翁，字会孟，别号须溪。庐陵灌溪（今江西省吉安市吉安县梅塘乡小灌村）人。南宋末年著名的爱国词人。 景定三年（1262）登进士第。他一生致力于文学创作和文学批评活动，为后人留下了可贵的丰厚文化遗产，遗著由子刘将孙编为《须溪先生全集》，《宋史·艺文志》著录为一百卷，已佚。', '1233', '1297', 10001, '2014-12-22 20:17:25'),
(10222, '周密', '周密，字公谨，号草窗，又号霄斋、蘋洲、萧斋，晚年号弁阳老人、四水潜夫、华不注山人，南宋词人、文学家。祖籍济南，先人因随高宗南渡，落籍吴兴（今浙江湖州），置业于弁山南。一说其祖后自吴兴迁杭州，周密出生于杭州。宋宝祐（1253~1258）间任义乌令（今年内属浙江）。宋亡，入元不仕。周密擅长诗词，作品典雅浓丽、格律严谨，亦有时感之作。能诗，擅书画。与吴文英（号梦窗）齐名，时人称为“二窗”。与邓牧、谢翱等往还。著述繁富，对保存宋代杭州京师风情及文艺、社会等史料，贡献很大。曾在吴兴家中设“书种”、“志雅”两座藏书楼，藏书42000余卷，及祖上三代以来金石之刻1500余种。但生前已散去不少。', '1232', '1298', 10001, '2015-01-02 11:26:16'),
(10223, '张炎', '张炎，南宋最后一位著名词人，字叔夏，号玉田，又号乐笑翁。祖籍凤翔成纪（今甘肃天水），寓居临安（今浙江杭州）。贵族后裔（循王张俊六世孙），前半生富贵无忧。1276年元兵攻破临安，南宋亡，张炎祖父张濡被元人磔杀，家财被抄没。此后，家道中落，贫难自给，曾北游燕赵谋官，失意南归，长期寓居临安，落魄而终。', '1248', '1320', 10001, '2014-12-22 21:19:56'),
(10224, '王沂孙', '王沂孙，生卒年不详，字圣与，又字咏道，号碧山，又号中仙，因家住玉笥山，故又号玉笥山人，会稽（今浙江绍兴）人，大约生活在1230年至1291年之间，曾任庆元路（路治今宁波鄞州）学正。入元，其词多咏物之作，间寓身世之感，意旨隐晦，著有《花外集》一卷，又名《玉笥山人词集》，后人易名为《碧山乐府》。', '?', '?', 10001, '2014-12-22 21:28:33'),
(10225, '彭元逊', '彭元逊，生卒年不详，字巽吾，庐陵（今江西吉安）人。景定二年（公元1261年）参加解试。与刘辰翁有唱和，宋亡不仕。存词20首。宋词三百首多有收录。', '?', '?', 10001, '2014-12-22 22:00:30'),
(10226, '姚云文', '姚云文，宋末元初知名文学家，字圣瑞，号江村，江西高安人。咸淳四年进士。入元，授承直郎，抚、建两路儒学提举。有《江村遗稿》，今不传。《全宋词》存词九首。', '?', '?', 10001, '2014-12-22 22:03:21'),
(10227, '卓文君', '卓文君，原名文后，西汉临邛（今四川邛崃）人，为四川临卭巨商卓王孙之女。汉代才女，中国古代四大才女之一、蜀中四大才女之一。姿色娇美，精通音律，善弹琴，有文名。卓文君与汉代著名文人司马相如的一段爱情佳话至今被人津津乐道。她也有不少佳，如《白头吟》，诗中“愿得一心人，白头不相离”堪称经典佳句。', '-175', '-121', 10013, '2015-02-05 20:10:34'),
(10228, '李东阳', '李东阳，字宾之，号西涯。祖籍湖广长沙府茶陵，因家族世代为行伍出身，入京师戍守，属金吾左卫籍。李东阳八岁时以神童入顺天府学，天顺六年中举，天顺八年举二甲进士第一，授庶吉士，官编修，累迁侍讲学士，充东宫讲官，弘治八年以礼部右侍郎、侍读学士入直文渊阁，预机务。立朝五十年，柄国十八载，清节不渝。官至特进、光禄大夫、左柱国、少师兼太子太师、吏部尚书、华盖殿大学士。死后赠太师，谥文正。', '1447', '1516', 10007, '2015-02-13 10:45:54');
